css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("ieeexplore.ieee.org") {
  
  body {
    color: #aaaaaa;
    background-color: #202020;
    background-image: none;
  }
  
  input[type=checkbox] { /* Checkboxes */
    filter: invert(80%);
  }
  
  input[type="text"], input[type="password"], textarea {
    border-color: #333333;
  }
  
  /* Page Bkgd */
  #article-page-hdr {
    background-color: #282828;
  }
  
  /* Topmost thin strip having 'IEEE.org | ... ' */
  .Metanav {
    background-color: #202020;
  }
  
  /* Top Header */
  .Header {
    background-color: #202020;
  }
  
  #loggedInLogo .branding-container {
    background-color: #202020;
    border: none;
  }
  
  .logo-ieee {
    filter: invert(97%);
  }
  
  /* Top Bar - 'Browse', 'My Settings', 'Get Help',... */
  .Toolbar {
    background: #282828;
    border-bottom-color: #666;
  }
  
  .Toolbar a {
    color: #ccc;
  }
  
  .Toolbar-item a:hover {
    color: #fff;
  }
  
  .Toolbar-button--primary { /* 'Browse' */
    background: #333333;
  }
  
  .Toolbar-button-wedge { /* 'Browse' */
    border-color: transparent transparent #333333 #333333;
  }
  
  .Menu--overlay {
    background-color: #333333;
  }
  
  .Menu--overlay a {
    color: #129bfa;
  }
  
  /* Top Bar - 'Browse' > 'Topics' */
  .Overlay-body {
    border-color: #17445A; /* from parent .Menu--overlay.Toolbar-subnav-menu */
  }
  
  #browse-by-topic a {
    background-color: #333333;
    color: #129bfa;
  }
  
  /* Search fields */
  .Search .Search-inputs input[type="text"] {
    background: none;
    background-color: #333333;
    border-color: #333333;
    color: #c3c3c3;
  }
  
  .Search .Search-control {
    filter: invert(80%);
    background-color: #fff;
    color: #aaaaaa;
  }
  
  .Search {
    background: #282828;
  }
  
  /* Search button */
  .Search-submit {
    background: #333333;
    border-color: #333333;
    color: #aaaaaa;
  }
  
  .Search-submit:hover {
    color: #fff;
  }
  
  .icon-search {
    filter: invert(80%);
  }
  
  /* Search suggestions dropdown - others */
  .Search-basic-typeahead.ui-autocomplete.ui-widget .item {
    color: #aaaaaa;
    background-color: #333333;
  }
  
  .Search-basic-typeahead.ui-autocomplete.ui-widget .item.active {
    color: #c3c3c3;
    border: none;
    background-color: #282828;
    background-image: none;
  }
  
  .ui-widget-content {
    border-color: #333333;
  }
  
  /* Search suggestions dropdown - Main page */
  .Search-basic-typeahead.ui-autocomplete.ui-widget .ui-menu-item a.ui-corner-all {
    color: #c3c3c3;
    background-color: #333333;
  }
  
  .Search-basic-typeahead.ui-autocomplete.ui-widget .ui-menu-item a.ui-corner-all:hover {
    color: #c3c3c3;
    background-color: #282828;
  }
  
  .Search-basic-typeahead.ui-autocomplete.ui-widget .ui-menu-item a.ui-state-focus {
    color: #c3c3c3;
    background-color: #282828;
  }
  
  /* 'Basic Search', 'Author Search', 'Publication Search' */
  .Search-control a {
    background: #333333;
  }
  
  .Search-control.active a {
    background: #3f3f3f;
  }
  
  /* 'Advanced Search', 'Other Search Options' */
  .Search-option-title {
    color: #aaaaaa;
  }
  
  .Search .Search-misc-container {
    color:  #666; /* acts as border-color */
  }
  
  /* 'Other Search Options' dropdown */
  .Search-menu.active {
    background-color: #3f3f3f;
  }
  
  .Search-menu .Menu--overlay a:hover {
    background-color: #282828;
  }
  
  /* Error Notification on Search */
  .Notification[data-notify-state="error"] {
    background-color: #202020;
    border-color: #333333;
  }
  
  /* Title */
  .document-title {
    color: #a7a7a7;
  }
  
  /* 'Related Articles' top-right rectangular box */
  .document .header-rel-art {
    background-color: #282828;
    border-color: #333333;
  }
  
  .document .header-rel-art .header-rel-art-title {
    color: #3f3f3f;
  }
  
  .document .header-rel-art .header-rel-art-item:not(:last-child) {
    border-bottom-color: #333333;
  }
  
  /* 'View all authors' band */
  .document .document-authors-banner {
    background-color: #333333;
  }
  
  /* 'Abstract', 'Authors', 'Figures', ... Pane */
  .tab-nav {
    background-color: #333333;
  }
  
  .tab-nav ul li.browse-pub-tab.active {
    background-color: #282828;
  }
  
  /* Citation Map */
  .modal-content {
    background-color: #282828;
  }
  
  .cmap-map-col.bg-img {
    filter: invert(97%);
  }
  
  .modal-close-container .modal-close {
    filter: invert(80%);
  }
  
  /* 'View all' having Page No.s */
  .carousel-controls {
    background-color: #282828;
  }
  
  /* 'Read the full document' message pane */
  .document .document-full-text-content .col-text .doc-ft-unavail {
    background-color: #333333;
  }
  
  .document .document-full-text-content .col-text {
    border-left-color: #666;
  }
  
  /* 'Download citations', 'View references',... Left rows */
  .document .doc-actions li a {
    color: #aaaaaa;
  }
  
  /* 'Share' */
  .document .doc-actions div a {
    color: #0470bb;
  }
  
  /* 'Related Articles' Left column */
  .document .related-articles {
    background-color: #282828;
  }
  
  a {
    color: #057fd4; /* #0470bb; */
  }
  
  a:hover {
    color: #129bfa;
  }
  
  /* bottom white band */
  .document .previous-next-nav-ctrl {
    background-color: #333333;
  }
  
  .document .previous-next-nav-ctrl .col-prev, 
  .document .previous-next-nav-ctrl .col-toc, 
  .document .previous-next-nav-ctrl .col-next {
    border-top-color: #666;
    border-bottom-color: #666;
  }
  
  /* Bottom */
  #LayoutWrapper, .Footer {
    background-color: #333333;
  }
  
  /* Message above Login fields on 'My Settings' > 'Search History' */
  .alert-message {
    background-color: #282828;
    border-color: #666;
    color: #aaaaaa;
  }
  
  .alert-message h4 {
    color: #aaaaaa;
  }
  
  /*** Search Results ***/
  
  /** 'Download PDFs', 'Export', 'Set Search Alerts' dropdowns **/
  .qtip-download-pdf, .qtip-export-download, .qtip-set-search-alerts {
    background-color: #333333;
    border-color: #666;
    color: #aaaaaa;
  }
  
  /* Search within results */
  .search-within-submit-button {
    background-color: #3f3f3f;
    color: #aaaaaa;
  }
  
  /* 'All Results' */
  .qtip-filter-paging, .qtip-filter-showtype-subscribed {
    background-color: #333333;
    border-color: #666;
    color: #aaaaaa;
  }
  
  .filter-popover-option:focus, .filter-popover-option:hover {
    background-color: #3f3f3f;
    color: #aaaaaa;
  }
  
  /* 'Download PDFs' */
  .downloadpdf-predl-table .downloadpdf-predl-table-row-dark {
    background-color: #282828;
  }
  
  .downloadpdf-predl-table .downloadpdf-predl-table-header {
    border-top-color: #666;
    border-bottom-color: #666;
  }
  
  /* 'Export' */
  .export-tab-content, .export-tab.active {
    background-color: #333333;
  }
  
  .export-tab {
    background-color: #282828;
  }
  
  .export-tab a {
    color: #aaaaaa;
  }
  
  .export-tab-form-section-bordered {
    border-bottom-color: #666;
  }
  
  /** Middle Pane for 'Displaying results 1-M of N for...' **/
  .Dashboard-section-gray {
    background-color: #333333;
  }
  
  .Dashboard-section-gray:last-child {
    border-bottom-color: #666;
  }
  
  /* 'Filters Applied' hide icon */
  .Dashboard-breadcrumbs-toggle-icon {
    filter: invert(40%);
  }
  
  /** Row item **/
  h2 > a.ng-binding.ng-scope {
    color: #0077b3; /* Mild blue for Bold text */
  }
  
  h2 > a.ng-binding.ng-scope:hover {
    color: #057fd4;
  }
  
  .List-results-items, .List-results-message {
    border-bottom-color: #666;
  }
  
  /* Row item - Abstract Arrow */
  .icon-caret-abstract:hover, 
  .List-results-items .abstract-control.active .icon-caret-abstract {
    filter: invert(40%);
  }
  
  /* Highlighted Search terms */
  .List-results-items .highlight, .Breadcrumb-title {
    color: #1f8000;
  }
  
  /* Search term cross on top */
  .Breadcrumb-close {
    background-color: #333333;
    color: #666;
  }
  
  /* Note on top */
  #acmNote, #blue-note, #ibmNote {
    background-color: #004d75;
    border-color: #666;
  }
  
  /** Ad column on right side **/
  .google-ad {
    display: none;
  }
  
  /*** Search Results for 'Popular Search Terms' -> click item ***/
  .StandardDictionary h1 {
    background-color: #0c4f70;
    color: #aaaaaa;
  }
  
  .icon-help {
    filter: invert(80%);
  }
  
  .StandardDictionary-list {
    background-color: #333333;
  }
  
  /*** Advanced Search Page ***/
  .advanced-search {
    background-image: none !important;
  }
  
  /* Row link */
  .refine-search .Inputs li, .refine-search td table td {
    color: #0470bb;
  }
  
  /** Advanced Keyword/Phrases **/
  
  /* Section headers */
  .refine-search .header {
    background-color: #3f3f3f;
  }
  
  .refine-search .DHTML .RevealControl {
    color: #aaaaaa;
  }
  
  /* 'Learn more about' on right */
  .arrowBullet li {
    filter: invert(80%);
  }
  
  .arrowBullet li a {
    color: #fb8f44; /* gets inverted! */
  }
  
  /*** Home Page ***/
  
  /* 'Search N items' above Search fields */ 
  .Search-count {
    color: #aaaaaa;
  }
  
  .Search-count a {
    color: #999;
  }
  
  /* Sliding Ads */
  .Carousel-info, .Carousel-media, #carousel-highlights .Carousel-slide {
    background-color: #333333;
  }
  
  .Carousel-nav-prev, .Carousel-nav-node, .Carousel-nav-next {
    filter: invert(80%);
  }
  
  /* Tab with 'Journals & Magazines', 'Conference Publications', 'Standards', 'Books & eBooks', 'Courses' */
  .Nav-item {
    border-left-color: #666;
  }
  
  .Nav-wrapper {
    border-bottom-color: #666;
  }
  
  /* 'Just Published', 'Most Popular' columns */
  .Media-box {
    background: #333333;
    border-color: #666;
  }
  
  .Media-articles-item, .Media-articles-item--endcap {
    border-top-color: #666;
  }
  
  /* Loading icon */
  img[alt="Loading Data..."] {
    filter: invert(80%);
  }
  
  /* Right-side white space */
  .Media-ad {
    background-color: #333333;
  }
  
  /* 'Journals & Magazines', 'Conference Publications', ... Tab */
  .Nav--home {
    background: #282828;
  }
  
  /*** Paper page ***/
  
  /* 'View Document' button */
  .document .document-header .document-banner .sip-modal-button {
    color: #c3c3c3;
  }
  
  .document .document-header .document-banner .document-banner-metric-container button.document-banner-metric:hover {
    background-color: #282828;
    color: #129bfa;
  }
  
  /* 'M Paper Citations', 'N Full Text Views', etc. messages */
  /* beside 'View Document' button */
  .document .document-header .document-banner .document-banner-metric-container button.document-banner-metric {
    background-color: #333333;
  }
  
  /* 'N Author(s)' on right of author names */
  .document .document-authors-banner .authors-count-container {
    border-right-color: #666;
  }
  
  /* 'Download citations', 'View References', 'Email',... on Left side */
  .document .doc-actions .doc-actions-item a {
    color: #aaaaaa;
  }
  
  .document .doc-actions .doc-actions-item:not(:last-child) {
    border-bottom-color: #666;
  }
  
  .icon-caret-down {
    filter: invert(40%);
  }
  
  .document .doc-actions .doc-actions-link .doc-act-icon-citations {
    opacity: 0.6;
  }
  
  /* dropdown for 'Contents' in middle */
  .qtip-popover.document-toc li a:hover {
    background-color: #3f3f3f;
    color: #0088cc;
  }
  
  /** Pop-up pane on 'Download citations' and 'Email' **/
  .qtip-popover {
    background-color: #333333;
    color: #aaaaaa;
  }
  
  .qtip-popover .header {
    color: #aaaaaa;
  }
  
  /* middle vertical bar */
  .qtip-document .download-citations section + section {
    border-left-color: #3f3f3f;
  }
  
  /* 'Cancel' button */
  .pure-button {
    background-color: #282828;
    color: #aaaaaa;
  }
  
  /* 'Download' button */
  .pure-button-primary {
    background-color: #069; /* default value */
    color: #aaaaaa;
  }
  
  
  /* Abstract Figure e.g. http://ieeexplore.ieee.org/document/7140733/ */
  .document .document-abstract .abstract-graphic img.abstract-graphic-img {
    filter: invert(20%); /* since most are white bkgd */
  }
  
  /* Magnifying glass, 'Aa' symbol on top of 'Abstract', 'Authors',... */
  .document .document-doc-buttons ul.tools li a {
    background-color: #333333;
    border-color: #666;
  }
  
  .document .document-doc-buttons ul.tools li:not(:first-child) a::after {
    background-color: #666; /* acts as border-color */
  }
  
  /* Magnifying glass search results */
  .document .document-search-items .highlight {
    color: #1f8000;
  }
  
  .ArticlePage #article .highlight {
    color: #2f7519;
    font-weight: none;
  }
  
  /* Right-side floating pane with 'Full Text',  'Abstract',  'Authors', 'Figures', ... */
  .document .document-doc-buttons ul.document-buttons li .btn-document {
    color: #c3c3c3;
  }
  
  /* Online sharing activity, on Right side */
  div#_altmetric_popover_el.altmetric-embed div#_altmetric_container.altmetric_container div#_altmetric_popover_inner .altmetric-popover-content {
    background-color: #333333;
  }
  
  /* Figures */
  .ArticlePage .img-wrap img {
    filter: invert(25%); /* since most are white bkgd */
  }
  
  .ArticlePage #article .figure .img-wrap {
    border-color: #666;
  }
  
  /* Figure Popup Pane, on click */
  .fig-win, .table-win {
    background-color: #333333;
  }
  
  /* References */
  .document .document-all-references .ref-details {
    background-color: #333333;
  }
  
  /* Keywords */
  .document .document-all-keywords .doc-all-keywords-list-item:not(:last-child) {
    border-bottom-color: #666;
  }
  
  /* Authors */
  .document .document-all-authors .author-container:not(:last-child) {
    border-bottom-color: #666;
  }
  
  /* Related Articles */
  .document .document-all-related-articles .doc-all-related-articles-list-item:not(:last-child) {
    border-bottom-color: #666;
  }
  
  /** Advertisement Pane, in the middle **/
  
  /* Bkgd */
  .Ads-leaderboard.middle {
    background-color: #333333;
    border-top-color: #333333;
  }
  
  /* Title */
  .ad-leaderboard-title {
    display: none;
  }
  
  /* Content */
  .ad-leaderboard-ad-container {
    display: none !important;
  }
  
  /*** Paper -> Read the full document ***/
  
  /* 'Quick Preview' top row of tabs */
  img#logo-glance {
    filter: invert(80%);
  }
  
  .ArticlePage #glance-hdr {
    background-image: none;
  }
  
  .ArticlePage #at-glance .panel {
    background-image: none;
    background-color: #333333;
  }
  
  /* 'Abstract', 'Authors', 'Figures',... content */
  .ArticlePage #at-glance .content {
    background-image: none;
    background-color: #282828;
  }
  
  .ArticlePage #at-glance H1 {
    color: #aaaaaa;
  }
  
  .carousel-item {
    border-color: #666;
  }
  
  /* 'Authors' tab content */
  img[alt="No Photo Available"] {
    filter: invert(80%);
  }
  
  .ArticlePage .author .img-wrap {
    background-color: #282828;
    border: none;
  }
  
  .ArticlePage .glance-authors .more A {
    background-image: none;
  }
  
  .ArticlePage .glance-authors .author {
    border-left-color: #3f3f3f;
  }
  
  /* On clicking 'Quick Abstract' bottom link */
  .ArticlePage .ref .snippet {
    background-color: #333333;
  }
  
  /* 'Figures' tab content */
  .figure.figure-preview {
    border-color: #666;
  }
  
  /* 'References' tab content */
  .ArticlePage #at-glance .ref {
    background-color: #333333;
  }
  
  .ArticlePage #at-glance .ref .footer A {
    background-image: none;
  }
  
  /* 'References' -> 'View All' Page */
  .document .document-all-references .reference-container:not(:last-child) {
    border-bottom-color: #666;
  }
  
  /* 'Citations' tab content */
  .togbut.disabled {
    color: #282828;
    background-color: #333333;
    border-color: #333333;
  }
  
  .togbut.disabled:last-child {
    border-right-color: #333333;
  }
  
  /* 'Keywords' tab content */
  .ArticlePage .glance-keywords .block {
    border-left-color: #3f3f3f;
  }
  
  .document .keywords-tab .doc-keywords-list-item {
    border-right-color: #666;
  }
  
  /* 'Keywords' -> 'View All' Page */
  .document .keywords-tab .doc-keywords-viewall-link {
    background-color: #0470bb;
  }
  
  a.doc-keywords-viewall-link.stats-keywords-link-viewAll {
    color: #ddd;
  }
  
  /* 'Metrics' tab content */
  .document .metrics-tab .metrics-info-container .usage-container .usage-info .usage-details-container {
    border-right-color: #666;
    border-left-color: #666;
    border-bottom-color: #666;
  }
  
  .document .metrics-tab .metrics-info-container .usage-container .usage-info .usage-tabs-nav {
    border-bottom-color: #666;
  }
  
  .document .metrics-tab .metrics-info-container .usage-container .usage-info .usage-tabs-nav ul.usage-tabs li.usage-tabs-link.active {
    border-right-color: #666;
    border-left-color: #666;
    border-top-color: #666;
  }
  
  .document .metrics-tab .metrics-info-container .usage-container .usage-info .usage-tabs-nav ul.usage-tabs li.usage-tabs-link.active {
    background-color: #3f3f3f;
  }
  
  .document .metrics-tab .metrics-info-container .usage-container .usage-info .usage-tabs-nav ul.usage-tabs li.usage-tabs-link > a {
    color: #aaaaaa;
  }
  
  /* 'Metrics' -> Table */
  .document .metrics-tab .metrics-info-container .usage-container .usage-info .usage-details-container .usage-details-chart {
    border-color: #666;
  }
  
  .document .metrics-tab .metrics-info-container .usage-container .usage-info .usage-details-container .usage-details-chart .usage-details-table-container .usage-details-table .usage-details-table-cellgroup .usage-details-table-month {
    background-color: #282828;
    border-right-color: #666;
    border-bottom-color: #666;
  }
  
  .document .metrics-tab .metrics-info-container .usage-container .usage-info .usage-details-container .usage-details-chart .usage-details-table-container .usage-details-table .usage-details-table-cellgroup .usage-details-table-count {
    border-right-color: #666;
  }
  
  /* 'Metrics' -> 'Citations' */
  .document .metrics-tab .metrics-info-container > div:not(:first-child) {
    border-left-color: #666;
  }
  
  /* Separator above Paper contents */
  .document .header-gray-line {
    border-top-color: #666;
  }
  
  /* 'Download PDF' */
  .ArticlePage .box-style-2 .cap-top {
    background-image: none;
  }
  
  .ArticlePage .pdf {
    filter: invert(80%);
  }
  
  /* 'JUMP' buttons */
  #FiguresPage .toc .jump-btn, .ArticlePage .toc .jump-btn {
    filter: invert(80%);
  }
  
  /* Hovering buttons - 'Quick Preview', 'Figures', 'Full Text', 'Footnotes', 'References', 'Authors', 'Cited By', 'Keywords', 'Corrections' */
  #FiguresPage #article-nav LI, .ArticlePage #article-nav LI {
    filter: invert(80%);
  }
  
  /* '+'/'-' Fold/Unfold toggle buttons */
  .ArticlePage .toggle-btn {
    filter: invert(80%);
  }
  
  /* 'JUMP' button dropdown */
  .ArticlePage .toc .body {
    background-color: #333333;
    background-image: none;
  }
  
  /* Reference link Popup */
  #ref_popup .header, #ref_popup .body, #ref_popup .footer {
    filter: invert(80%);
    color: #333; /* gets inverted! */
  }
  
/*   #ref_popup .header {
    background: #333;
  } */
  
  #ref_popup .footer > a {
    color: #bb4f04; /* gets inverted! */
  }
  
  /* Formulae */
  img[alt="Formula"] {
    filter: invert(80%);
  }
  
  .ArticlePage .formula .tex {
    background-color: #282828;
  }
  
  .ArticlePage .formula .link, .ArticlePage .formula .open_lnk {
    background-image: none;
  }
  
  /* Formulae - MathJax popup */
  .MathJax_Menu, #MathJax_About, #MathJax_Help, #MathJax_HelpContent {
    background-color: #333333;
    color: #aaaaaa;
    border-color: #666;
    -webkit-box-shadow: 0px 10px 20px #3f3f3f; /* except color, all default */
    -moz-box-shadow: 0px 10px 20px #3f3f3f; /* except color, all default */
  }
  
  .MathJax_MenuRule {
    border-top-color: #666;
  }
  
  span[style="display: inline-block; text-align: left; font-size: 80%; max-height: 20em; overflow: auto; background-color: rgb(228, 228, 228); padding: 0.4em 0.6em; border: 1px inset;"] {
    background-color: #333333 !important;
  }
  
  #MathJax_HelpClose span, .MathJax_MenuClose span {
    background-color: #333333;
  }
  
  #MathJax_HelpClose {
    color: #aaaaaa;
  }
  
  /* References - 'Show Context', 'Quick Abstract', 'Full Abstract',.. */
