css:
@-moz-document url-prefix("https://privnote.com/"), url-prefix("http://privnote.com/"), url-prefix("http://privnote.com") {
body {
    background-color: #222!important
}
#header_bg {
    background-color: #670000;
    border-bottom: 14px solid #131313;
}
#header h2 {
    color: #a0a0a0;
}
#content {
    background-color: #222;
}
#content h1 {
    color: #d8d8d8;
}
p {
    color: #a0a0a0;
}
a {
    color: #b90000;
}
h1,
h2,
h3,
h4 {
    color: #d8d8d8;
}
li {
    color: #d8d8d8;
}
/*TextArea*/

#content textarea {
    background-color: rgba(19, 19, 19, 0.25);
    border: 2px solid #131313;
    background-image: none;
    color: #b7b7b7;
}
#note_link_input {
    background-color: #670000;
    font-weight: bold;
    color: #ffffff;
    border-bottom: solid 2px #131313;
}
label span.caption {
    background: #670000;
    color: #ffffff;
}
input[type="text"],
input[type="password"],
textarea {
    border: none;
}
/*HELP*/

.help_button {
    border: 1px solid #131313;
    color: #b9b9b9;
    background-color: rgba(19, 19, 19, 0.25);
}
/*Buttons*/

.primary_button {
    background-color: #670000;
    border: 1px solid #131313;
}
.col .primary_button,
.col .small_button,
.col .button {
    color: #d6d6d6;
}
.button {
    background-color: rgba(19, 19, 19, 0.25);
    border-radius: 5px;
    border: 1px solid #131313;
}
.small_button {
    background-color: rgba(19, 19, 19, 0.25);
    border-radius: 5px;
    border: 1px solid #131313;
}
.danger {
    background-color: #c70000;
    color: #ffffff;
    font-weight: bold;
}
/*Language Select*/

#footer select {
    color: #888;
    background-color: rgba(19, 19, 19, 0.25);
    border-color: #131313;
}
/*Unsuported*/

#unsupported {
    background-color: rgba(19, 19, 19, 0.25);
    color: #a0a0a0;
}
/*WarningBlock*/

.warning_block {
    color: #d8d8d8;
    background-color: #670000;
}
/*Options*/

#content input[type="text"],
#content input[type="password"],
#content select {
    background-color: rgba(19, 19, 19, 0.25);
    color: #b7b7b7;
    border: 1px solid #131313;
}
label {
    color: #737373;
}
#advanced_options_tip {
    color: #737373;
    font-weight: bold;
}
/*End*/

#footer {
    background-color: #222;
}
#footnote {
    font-size: 0.7rem;
    padding: 1em;
    color: #b5141a;
}
}
