@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("imdb.com") {
  
  .recently-viewed, #footer, div.article.contribute, #social-share-widget, div.aux-content-widget-2:nth-child(19), div.article:nth-child(22), div.aux-content-widget-2:nth-child(37), div.aux-content-widget-2:nth-child(32) {
