    color: #797979;
    border: none;
  }
  td#img_primary img {
    border: none;
  }
  .redesign .article.highlighted {
    background: none;
    border: none;
  }
  .pswp__item {
    background-color: #17181A;
    border: none;
    color: #797979;
  }
  .mediaviewer__footer, .mediaviewer__header {
    background-color: #17181A 35%;
    color: #797979;
  }
  
  a:hover, a:link, #name-job-categories > a:nth-child(1) > span:nth-child(1) {
    color: #7B4895 !important;
    text-decoration: none;
  }
  #meterSeeMoreRow, div.aux-content-widget-3.links.subnav, span.pro-list-title, .highlighted {
