#accordion .accordion-header,
#tn15lhs .photo,
#tn15main table tr.odd,
#content-2-wide #sidebar,
.article,
.odd,
.newsWidget ul.newsTabs a,
.rec_details .rec-jaw-upper,
.rec_details .rec-jaw-lower,
.rec_details .rec-jaw-lower > div.rec-jaw-teeth,
.board .comment .metadata,
.boards-navigation,
.boards.history .odd, 
.boards.pms .odd, 
.boards.favorites .odd, 
.boards.friends .odd, 
.category .odd, 
.threads .thread.odd,
.listo ul.list_tabs li.active a,
.results tr.odd,
.seasonAndYearNav,
.action-box,
.redesign .episode-nav,
.watchbar2 section,
.article.live_event .row.odd,
