#wrapper #pagecontent #content-2-wide #sidebar .list-preview.odd,
#latestHeadlines,
#navbar div.quicksearch_dropdown_wrapper,
#navbar select:hover,
.poll-featured,
.yn,
.article.live_event .row.odd,
.heroNav,
.heroNavItemCaption {
        background-color: #17181A !important;
        border: none;
  }

.aux-content-widget-2, .aux-content-widget-3, .article.on-tv, .aux-content-widget-4 {
	background-color: #17181A !important;
  background: #17181A !important;
  border: none;
  }
    
.listo .header .nav {
	background-image: none; 
	background-color: #17181A; 
	border: none;
  }

#filmography .head {
        background-color: #17181A;
        border-top: #17181A 1px solid;
  text-shadow: none;
  border: none;
  }
  .title_wrapper > h1:nth-child(1), #navbar, .message_box .info h2, #sidebar h3, h3, h3:hover {
