    color: #797979 !important;
    border: none;
  }
  .pagination a:hover {
    background-color: #353535;
    color: #797979 !important;
    border: none;
  }
  .category {
    border: none;
  }
  #content-2-wide #sidebar, .aux-content-widget-2, #top_rhs_after {
    background-color: #17181A !important;
