    background-color: #17181A !important;
    background: #17181A !important;
    border-top: #17181A 1px solid !important;
  }
  ul.main_nav li.spacer, ul.quicklinks li.subnav_selected {
    background: #353535 !important;
  }
  #nb_search .primary.btn:hover, #nb_search .primary.btn, #navbar button.btn, button.primary:hover, .btn.primary, .btn:hover, button.btn, button.primary {
