css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("8ch.net") {
  
  /*
  author: 8cr14mov
  version date: Monday 12, December, 2016.
  function/ purpose: 8cr14mov's aesthetic overhaul 
  */

  body {
    background: #17181A !important;
    color: #797979 !important;
  }
  div.post.reply, .desktop-style div.boardlist:not(.bottom), form table tr th, .dropzone, form table tr td, div.pages {
    background: #17181A;
    color: #797979;
    border: none;
  }
  /*** ENABLE/ DISABLE POST BORDERS ***
  div.post.reply {
    border: 1px solid #252525 !important;
  }
  /*** ENABLE/DISABLE POST BORDERS ***/
  a, div.banner a {
    color: #7B4895;
    text-decoration: none;
  }
  a:hover, a:visited, div.banner a:hover, .intro span.subject {
    color: #AA79C3;
    text-decoration: none;
  }
  div.banner {
    background-color: #252525;
    color: #797979;
  }
  h1, .file-hint:hover, .options_tab_icon {
    color: #797979;
  }
  #favorite-star, .required-star, .options_tab_icon.active {
    color: #7B4895 !important;
  }
  hr {
    border-top: 1px solid #252525;
  }
  input, input[type="text"], input[type="password"], textarea {
    border: 1px solid #252525;
  }
  span.name, .intro a.email span.name {
    color: #B85DA6;
  }
  span.sage {
    color: #64B85D;
  }
  #options_tablist, #options_div {
    background-color: #17181A;
    color: #797979;
    border-right: 1px solid #252525;
    border: none;
  }
  .theme-catalog div.thread:hover {
    background-color: #353535;
    border-color: #353535;
    border: none;
  }
  .theme-catalog div.thread {
    background-color: #252525;
    border-color: #252525;
    border: none;
  }
  span.heading {
    color: #8D3235;
  }
  .quote {
    color: #658D37;
  }
  span.spoiler {
    background-color: #353535;
    color: #353535;
  }
  span.spoiler:hover {
    color: #AA79C3;
  }
  div.post.reply div.body a {
    color: #7B4895;
  }
  .inline {
    border: 1px dashed #252525;
  }
  div.post.reply has-file, div.post.reply post-hover {
    border: none;
    box-shadow: none;
  }
  div.post.reply.highlighted {
    background-color: #252525;
  }
  div.pages a.selected {
    color: #797979 !important;
  }
  p.fileinfo::before {
    opacity: 0.4 !important;
    filter: alpha(opacity=40) !important;
  }
  ul {
    background: #353535 !important;
    border: none !important;
  }
  .box, #velocious_main > section:nth-child(1) > ul:nth-child(5), li.col.col-12, td.board-uri, td.board-title, td.board-pph, td.board-unique, td.board-tags, td.board-max, table.board-list-table .board-cell {
    background: #252525 !important;
    color: #797979 !important;
    border: none !important;
  }
  .box-title.col.col-6, .box-title.col.col-2, h2.box-title, th.board-uri, th.board-title, th.board-pph, th.board-unique, th.board-tags, th.board-max, #board-list-more {
    background: #353535 !important;
    color: #797979 !important;
    border: none !important;
  }
  textarea#body, input, textarea {
    background: #17181A !important;
    color: #797979 !important;
  }
}
