css:
/*
Author: 8cr14mov
Purpose: Darken and clean dafont interface.
*/

@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("dafont.com") {
  body, .dlbox, .dl, .perso select, .form_elt, .titlebar {
    background-color: #17181A;
    color: #797979;
    border: none;
  }
  div, a, .menu a, .menu a:hover, .login a, .login a:hover, a.flag, .dl:hover, .donate, .donate:hover, .black, .black:hover, .help, .lv2right a, a.tdn, a:hover, p, table td {
    color: #797979 !important;
    border: none !important;
    background-image: none;
    text-decoration: none;
  }
  div.tdn, div.points, div.dfsmall.tdh, div.dfxsmall, div.dfsmall:nth-child(30) > div:nth-child(3) {
    display: none !important;
  }
  .dfbg, .dfbg a, h1, span.filename, .select, .form_elt_bg, .new_date, .container > div:nth-child(5) > div:nth-child(17) > div:nth-child(1), .perso select:hover, h3, .container > div:nth-child(5) > div:nth-child(4) > form:nth-child(1) > table:nth-child(3) > tbody:nth-child(1) > tr:nth-child(1) {
    background-color: #252525 !important;
    background: #252525 !important;
    color: #898989 !important;
    border: none !important;
  }
  img, div.preview {
    -webkit-filter: invert(1);
    filter: invert(1);
  }
  #top > img:nth-child(1) {
    -webkit-filter: grayscale(1);
    filter: grayscale(1);
  }
  #menuthemes table, .container > div:nth-child(5) > div:nth-child(4) > form:nth-child(1) > table:nth-child(3) {
    border: none !important;
  }
  .colthemes {
    border-left: none;
  }
  .container > div:nth-child(2) > div:nth-child(2), div.dfsmall:nth-child(30) > div:nth-child(1) {
    background-image: none !important;
  }
  .catpp a:hover {
    background-color: #252525 !important;
    color: #797979 !important;
  }
  #menuthemes b {
    color: #7B4895 !important;
  }
}
