css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("juick.com") {

#header .text, .inp, #dialogl, #dialogn, .msg-comment textarea, #hlinks .button, #hlinks .button ul, strong, .msg-comment .reply, .msg-comment input, textarea, .newmessage, input[type="text"], input[type="submit"], input[type="file"], input[type="file"]:after, input[type="file"] > input[type="button"], .dialogl, .signinput, .signsubmit {background:#444 !important; color:#EABA6E !important;}


/*########################################] ALL [####################*/
html {
	background: #444444;
	color:#BABDB6
}
body {
	width:1000px;
	margin:0 auto
}
a {
	text-decoration:none;
	color:#EABA6E
}

/*########################################] Pictures [####################*/
#logo a {
	display:block;
	text-indent:100%;
	white-space:nowrap;
	overflow:hidden;
	border:0;
	background:url("https://i.juick.com/p/2844989-1.png") no-repeat;
	background-size:cover;
}
.msg-menu>a {
	display:block;
	vertical-align:top;
	background:url("https://i.juick.com/p/2844989-2.png") no-repeat
}
#mtoolbar div {
	display:inline-block;
	width:16px;
	height:16px;
	background:url("https://i.juick.com/p/2844989-3.png") no-repeat;
	vertical-align:middle;
	margin:5px
}
/*########################################] Цитирование [####################*/
q:after,q:before {
	content:""
}
q {
	border-left:1px dashed #999;
	margin:8px 0 8px 8px;
	padding-left:8px;
	display:block;
	color:#999
}

/*########################################] Header [####################*/
body>header {
	width:1000px
}
body>header a {
	color:#999;
	border-bottom:1px solid #444;
	font-size:13pt
}
	
/*########################################] 1ая строка [####################*/
nav#global {
	float:left
}
nav#global li {
	display:inline-block;
	margin:20px 3px 3px 5px
}
#search {
	float:right;
	margin:17px 8px 8px 0
}
#search input {
	background:#444;
	border:1px solid #999;
	padding:4px
}
#logo {
	float:left;
	margin:8px 8px 0 8px
}

/*########################################] 2ая строка [####################*/
#headdiv {
	clear:both;
	margin:0 0 2px;
	padding:0 5px;
	background:#444;
	border-top:1px solid #444;
	border-bottom:1px solid #444;
	position:relative
}
#headdiv li {
	display:inline-block;
	margin:8px 3px 3px 8px
}
	
/*########################################] Новое сообщение [####################*/
#newmessage {
	background:#333;
	padding:5px;
	margin-bottom:5px
}
#newmessage textarea {
	border:1px solid #333;
	padding:4px;
	width:688px;
	resize:vertical;
	min-height:14pt;
	height:14pt;
	margin:0 0 5px
}
#newmessage input {
	border:1px solid #333;
	padding:2px 4px;
	margin:5px 0
}
#newmessage>div {
	display:none
}
#newmessage .img,#newmessage .tags {
	width:500px
}
#newmessage .subm {
	width:150px;
	background:#333
}	
	
/*########################################] Пост [####################*/
.msg-avatar {
	float:left;
	width:48px;
	height:48px;
	margin-right:10px;
	margin-bottom:7px
}
article {
	margin-bottom:8px;
	padding:8px;
	line-height:130%;
	background:#333
}
article>aside {
	margin-right:10px;
	float:left
}
article>aside img {
	width:48px;
	height:48px
}
article header.t {
	float:right;
	text-align:right;
	margin-top:-1px
}
article time {
	color:#999;
	font-size:10pt;
	border-bottom:1px solid #333
}
article p {
	margin:5px 0 15px;
	clear:left
}
article p.i {
	text-align:center
}
article p.ir {
	float:right;
	margin-left:10px;
	margin-bottom:10px
}

article p.ir img {
	max-width:200px;
	max-height:200px
}
article .irbr {
	clear:right
}
article>nav.l {
	display:inline-block;
	font-size:10pt
}
article>nav.l a {
	color:#999;
	border-bottom:0px solid #999;
	margin-right:20px
}
article>nav.s {
	display:inline-block;
	text-align:right;
	float:right
}
article>nav.s a {
	color:#999;
	vertical-align:middle;
	font-weight:700
}
article a.likes {
	padding-left:20px
}
article a.replies {
	margin-left:18px
}
article footer.comm {
	margin:13px 0 0
}
article textarea {
	width:530px;
	padding:2px;
	resize:vertical;
	vertical-align:top;
	min-height:12pt;
	height:12pt;
	border:0
}
article input {
	width:50px;
	margin-left:6px;
	vertical-align:top;
	border:1px solid #999;
	background:#999;
	color:#999
}

/*########################################] Tags [####################*/
article .tags {
	margin-top:3px
}
.msg-tags {
	margin-top:5px
}
.msg-tags>a,article .tags>a {
	font-size:11pt;
	display:inline-block;
	margin-bottom:5px;
	margin-right:3px;
	padding:0 3px;
	border:1px solid #444;
	color:#999
}
	
.msg-cont {
	background:#333;
	margin-bottom:8px;
	padding:8px;
	width:640px;
	line-height:130%
	}
.msg {
	margin:8px 0 8px	
}

/*########################################] Читать дальше [####################*/
.page {
	text-align:center;
	padding:5px;
	background:#333
}
	
/*########################################] Комментарии [####################*/	
#mtoolbar {
	width:640px;
	background:#444;
	border-top:1px solid #444
}
.title2 {
	padding:5px 5px;
	margin:8px 0;
	background:#444
}
.title2-right {
	float:right;
	margin:3px 340px 3px 3px;
	line-height:8px
}
#content .title2 h2 {
	font-size:x-large;
	margin:0
}

/*########################################] Диалоги [####################*/
.dialoglogin {
	width:300px;
	background:#333;
	padding:25px
}
.dialogshare {
	padding:20px;
	background:#333;
	border:1px solid #444;
	min-width:300px;
	overflow:auto
}
.dialogl {
	padding:5px;
	margin:3px 0 20px;
	border:1px solid #ddd;
	background:#f5f5e9
}
.dialogshare li {
	float:left;
	margin:5px 10px 0 0
}
	/*Шрифт поста
article > p, .msg-txt, input, textarea {
	font-size: 12pt;
}*/
}
