css:
@-moz-document domain("adarkroom.doublespeakgames.com") {
body {
	background-color: #101010;
	color: #BBB;
	
	font-family: "Segoe UI", Segoe, sans-serif;
	font-size: 14px;
}

div.headerButton {
	border-left: 1px solid #BBB;
}

div#notifyGradient {
	background-color: #101010;
	background: -webkit-linear-gradient( rgba(255, 255, 255, 0) 0%, rgba(16, 16, 16, 1) 100% );
	background: linear-gradient( rgba(255, 255, 255, 0) 0%, rgba(16, 16, 16, 1) 100% );
	filter: alpha( Opacity=0, FinishOpacity=100, Style=1, StartX=0, StartY=0, FinishX=0, FinishY=500 );
}

div#saveNotify {
	background: #101010;
}

.eventPanel:before {
	background-color: #101010;
}

.eventTitle:after {
	background-color: #101010;
}

div.tooltip {
	background-color: black;
	border: 1px solid black;
	box-shadow: none;
}

div#population {
	background-color: #101010;
}

div#village:before {
	background: #101010;
}

div#village {
	border: 1px solid #BBB;
}

div#stores:before {
	background: #101010;
}

div#stores {
	border: 1px solid #BBB;
}

div#weapons:before {
	background: #101010;
}

div#weapons {
	border: 1px solid #BBB;
}

div#bagspace {
	background-color: #101010;
}

div#outfitting:before {
	background: #101010;
}

div#perks {
	border: 1px solid #BBB;
}

div#perks:before {
	background-color: #101010;
}

div#outfitting {
	border: 1px solid #BBB;
}

#bagspace-world {
	border: 1px solid #BBB;
}

div.supplyItem {
	border: 1px solid #BBB;
}

#backpackTitle {
	background-color: #101010;
}

#backpackSpace {
	background-color: #101010;
}

#healthCounter {
	background-color: #101010;
}

#map {
	border: 1px solid #BBB;
}

#map .landmark {
	color: #BBB;
}

#dropMenu {
	background: none repeat scroll 0 0 #101010;
	box-shadow: none;
}

div.button {
	border: 1px solid #BBB;
}

div.button.disabled, div.button.disabled:hover {
	border-color: #808080;
	color: #808080;
	font-style: italic;
}


.upBtn:after, .upManyBtn:after {
	border-color: transparent transparent #101010;
}

.upBtn:before, .upManyBtn:before {
	border-color: transparent transparent #BBB;
}

.upBtn.disabled:before, .upManyBtn.disabled:before {
	border-color: transparent transparent #555;
}

.dnBtn:after, .dnManyBtn:after {
	border-color: #101010 transparent transparent;
}

.dnBtn:before, .dnManyBtn:before {
	border-color: #BBB transparent transparent;
}

.dnBtn.disabled:before, .dnManyBtn.disabled:before {
	border-color: #555 transparent transparent;
}

body.noMask .eventTitle {
	background-color: #101010;
	color: #BBB;
}

body.noMask .eventTitle:after {
	background-color: #101010;
}

body.noMask .eventPanel {
	background-color: #101010;
}

body.noMask #description {
	color: #101010;
}

body.noMask #buttons > .button {
	border: 1px solid #101010;
	color: #BBB;
}

#stars > div, #starsBack > div {
	color: black;
}

.endGame {
	color: #BBB;
}

#theEnd {
	color: #BBB;
}

span.lightsOff.menuBtn, span.appStore.menuBtn {
	display: none;
}
}
