css:
@namespace url(http://www.w3.org/1999/xhtml);

/*
^(https?:((\/\/)|(\\\\)))?(www)?\.?reddit\.(\w+)(\/|\\)r(\/|\\)anime((\/|\\).+)*
*/

@-moz-document regexp('(https?:(\/\/)|(\\\\\\\\))?(www\.)?reddit(\..+)(\/|\\\\)r(\/|\\\\)anime(\/(.+)?)?') {
  #header {
    background: linear-gradient(#334455, #3A4B5C) !important;
    border-bottom-color: #394A5B !important;
  }
  #header-bottom-left .tabmenu li a {
    border: 0px solid #667788 !important;
    border-bottom: 0px solid !important;
    background: #667788;
    color: lightsteelblue !important;
    -moz-border-radius: 0px;
    -webkit-border-radius: 0px;
    transition: border 0.5s, background 0.25s, color 0.25s;
  }
  #header-bottom-left .tabmenu li:last-child a {
    border-top-right-radius: 12px;
  }
  #header-bottom-left .tabmenu li:first-child a {
    border-top-left-radius: 12px;
  }
  #header-bottom-left .tabmenu li a:hover {
    border-bottom-color: rgba(0,0,0,0);
  }
  #header-bottom-left .tabmenu li.selected a {
    border: 0px solid #445566 !important;
    border-bottom: 0px solid !important;
    background: #445566;
    color: steelblue !important;
    transition: border 0.5s, background 0.25s, color 0.25s;
  }
  #header-bottom-left .tabmenu li.selected a:hover {
    border: 0px solid #556677 !important;
    border-bottom: 0px solid #667788;
    background: #556677;
    color: lightsteelblue !important;
  }
  #header-img {
    left: 8px;
  }
  .pagename {
    font-weight: normal;
    font-family: Segoe UI Light, Century Gothic;
    font-size: 4em;
    font-variant:small-caps;    
  }
  .pagename a {
    transition: color 0.25s, text-shadow 0.25s;
  }
  .res-nightmode .res-show-link {
    background-color: #343434;
    color: white;
  }
  .res-nightmode .pagename a:hover {
    text-decoration: none;
    text-shadow: 0px 0px 16px white;
    color: white;
  }
  .res-nightmode .link.promotedlink, .res-nightmode .link.promotedlink.promoted{
    background: #162020 !important;
    border-color: #263030 !important;
  }
  .res-nightmode a, .res-nightmode .md a, .res-nightmode .share-button .option, .res-nightmode #subscribe a, .res-nightmode .share .option, .res-nightmode .wired a, .res-nightmode .side a, .res-nightmode .link .score.dislikes, .res-nightmode .linkcompressed .score.dislikes, .res-nightmode a[rel="tag"], .res-nightmode .dsq-help, .res-nightmode #authorInfoToolTip h3 a, .res-nightmode .reddiquette, .res-nightmode .parent .author, .res-nightmode .parent .subreddit, .res-nightmode .combined-search-page .search-result a, .res-nightmode .combined-search-page .search-result a > mark, .res-nightmode .hoverHelp, .res-nightmode .deepthread a, .res-nightmode .policy-page .md h1 a, .res-nightmode .policy-page .md h3 a {
    transition: color 0.1s;
  }
   .res-nightmode a:hover, .res-nightmode .md a:hover{
    color: lightseagreen;
  }
  .res-nightmode .hover-bubble.multi-selector .create-multi input[type="text"]::-moz-placeholder {
    color: lightsalmon !important;
  }
  .res-nightmode .titlebox .usertext .md > h1 {
    color:aliceblue;
  }
    .res-nightmode #sr-header-area , .res-nightmode #sr-more-link, .res-nightmode div#RESShortcutsEditContainer,  .res-nightmode div#RESShortcutsSort, .res-nightmode div#RESShortcutsRight, .res-nightmode div#RESShortcutsLeft, .res-nightmode div#RESShortcutsAdd, .res-nightmode div#RESShortcutsTrash {
    background: #242424 !important;
    color:lightgray !important;
  }
  .res-nightmode p > a[href="/s"] {
    background: darkred;
  }
  .res-nightmode p > a[href="/s"]::after {
    background: black;
  }
  .link {
    padding-bottom:8px;
    padding: 4px;
    box-sizing: border-box;
  }
  .res-nightmode .linkflair-green .title a.title:link {
    color: lightpink !important;
    background: green;
    transition: color ease-out 0.25s , background-color ease-out 0.25s;
  }
  .res-nightmode .linkflair-green .title a.title {
    color: pink !important;
    background: darkgreen;
    transition: color ease-out 0.25s , background-color ease-out 0.25s;
  }
  .res-nightmode .linkflair-green .title a.title:hover {
    color: antiquewhite !important;
    background: seagreen;
  }
  .res-nightmode .title a.title {
    font-family: Segoe UI, Helvetica, Arial;
  }
  .res-nightmode .title a.title:link {
    color: steelblue !important;
    transition: color ease-out 0.25s;
  }
  .res-nightmode .title a.title {
    color: slateblue !important;
    transition: color ease-out 0.25s;
  }
  .res-nightmode .title a.title:hover {
    color: lightsteelblue !important;
  }
  .res-nightmode .title a.title[href*="fanart"]:link {
    color: green !important;
    transition: color ease-out 0.25s;
  }
  .res-nightmode .title a.title[href*="fanart"]:hover {
    color: seagreen !important;
  }
  .res-nightmode .title a.title[href*="fanart"] {
    color: darkolivegreen !important;
  }
  .res-nightmode .title a.title[href*="spoil"] {
    color: firebrick !important;
    transition: color ease-out 0.25s;
  }
  .res-nightmode .title a.title[href*="spoil"]:link {
    color: #F01010 !important;
    transition: color ease-out 0.25s;
  }
  .res-nightmode .title a.title[href*="spoil"]:hover {
    color: salmon !important;
  }
  .res-nightmode .odd , .res-nightmode .even {
    transition: background-color ease-out 0.25s;
  }
  .res-nightmode .odd:hover , .res-nightmode .even:hover {
    background: #1A1C1E;
  }
  .res-nightmode {
    transition: background 0.5s;
  }
  .res-nightmode .RES-keyNav-activeElement {
    box-sizing: border-box;
    color: lightsalmon;
  }
  .res-nightmode .RES-keyNav-activeElement, .res-nightmode .RES-keyNav-activeElement .md-container {
    background: #303030 !important;
  }
  .res-nightmode .titlebox {
    background: hsl(0,0%,15%) !important;
    border-color:#688AAD !important;
  }
  .res-nightmode .side .md a {
    color: steelblue;
  }
  .res-nightmode .side .md a:hover {
    color: lightsteelblue;
  }
  .res-nightmode .side .morelink a {
    color: orange;
    text-shadow: orangered 0 1px 0;
  }
  .res-nightmode .side .morelink a:hover {
    color: orangered;
    text-shadow: red 0 1px 0;
  }
  .res-nightmode .side .sidecontentbox, .res-nightmode .side .sidecontentbox .content, .res-nightmode .side .linkinfo {
    background-color: #242424 !important;
    border-color:#688AAD;
    color: lightgray;
  }
  .res-nightmode .side h3 {
    background-color:#688AAD;
    color: #ddd !important;
  }
  .res-nightmode .footer {
    background: #141414 !important;
    color: lightgray;
    border-radius: 2px;
  }
}
