/******************* REKLAM ***********************/ 
/*(Ändra display:block TILL display:none ifall du inte vill ha reklam */
.adModule-1 {display:block;}
.adOutsider-1{margin-left: 107px;display: block;}
.ad img{
    display: block;
    width: 324px;
    height:auto; 
}
.ad.adPanorama img{
    display: block;
    width: 1000px;
    height: auto;
}
.adFooter{display:none;}
/******************* BLANDAT ***********************/ 
p, h1, h2, h3, h4, h5{font-family: 'Roboto', sans-serif;color:black;}
html,
body,
p {
    font-size: 14px;
    font-weight: 400;
    line-height: 20px;
}
.mainRecentThreads { display: none;} /*Göm "Nytt i forumet" på starsidan längst ner*/
.mainRecentNews{display:none;} /*Göm "Senaste nyheterna" på starsidan längst ner*/
.mainRecentJobs{display:none;} /*Göm "Nya platsannonser" på starsidan längst ner*/

/******************* MENY ***********************/ 
.mainMenu .menu li {
    border-radius: 0px;
}
.subMenu .menu li {
    border-radius: 0px;
}
.menuItems{white-space:nowrap;}

/******************* FORUM ***********************/ 
.forumPost {
    box-shadow: 0 2px 2px 0 rgba(0, 0, 0, .14), 0 3px 1px -2px rgba(0, 0, 0, .2), 0 1px 5px 0 rgba(0, 0, 0, .12);
    background: #FFF;
}
.forumPost .cell.alt2 {
    background: #FFF;
}
.forumPost.isLinked {
    box-shadow: 0 2px 2px 0 rgba(0, 0, 0, .14), 0 3px 1px -2px rgba(0, 0, 0, .2), 0 1px 5px 0 rgba(0, 0, 0, .12);
    border: 0;
}
.forumPost .controls .button .multiquote {
    background-color: #ff4081;
    color: #FFF;
}
.forumTable {
    box-shadow: 0 2px 2px 0 rgba(0, 0, 0, .14), 0 3px 1px -2px rgba(0, 0, 0, .2), 0 1px 5px 0 rgba(0, 0, 0, .12);
}
.forumTable .cell,
.forumTable .cell.alt1 {
    background: #FFF;
    border-bottom: #d7d5d1 2px solid;
}
.forumTable .cell.alt2 {
    background: #FFF;
    border-bottom-color: #e7e5e0;
}
.forumPost.isGuru .controls .button.like .score,
.forumPost.isLiked .controls .button.like .score {
    color: #d96b0f;
    margin-right: 10px;
}
.ninjaSearch {margin-right: 60px;}
.score{color: #F4511E;}
.forumPost .details .activity.online {
    color: #d96b0f;
    font-size: 28px;
}
.marketFilterBox{
    background: #FFF;
    box-shadow: 0 2px 2px 0 rgba(0, 0, 0, .14), 0 3px 1px -2px rgba(0, 0, 0, .2), 0 1px 5px 0 rgba(0, 0, 0, .12);
    border:none;
    border-radius: 0px;
}
.whiteFilterBox legend {
    color: #6a6a6a;
    font-weight: 700;
    padding-top: 27px;
}
    
.forumPost .details .activity{
    font-size: 28px;
}

/******************* KNAPPAR ***********************/ 
.isDisabled {
    color: #000;
    background: initial;
}
.button,
.tanukiButton,
.tanukiSelect {
    border: none;
    border-radius: 2px;
    color: #000;
    position: relative;
    height: 36px;
    margin: 0;
    min-width: 64px;
    padding: 0 16px;
    display: inline-block;
    font-family: "Roboto", "Helvetica", "Arial", sans-serif;
    font-size: 14px;
    font-weight: 500;
    text-transform: uppercase;
    letter-spacing: 0;
    overflow: hidden;
    will-change: box-shadow;
    transition: box-shadow .2s cubic-bezier(.4, 0, 1, 1), background-color .2s cubic-bezier(.4, 0, .2, 1), color .2s cubic-bezier(.4, 0, .2, 1);
    outline: none;
    cursor: pointer;
    text-decoration: none;
    text-align: center;
    line-height: 36px;
    vertical-align: middle;
}
button,
.button {
    background-image: none;
    background: inherit;
}
.button.isDisabled {
    background: initial;
    color: rgba(0, 0, 0, .26);
    background-color: transparent;
    border: none;
    cursor: not-allowed;
}
.forumSearch .tanukiTextbox {
    width: inherit;
    margin: 0;
}
.forumSearch .tanukiTextbox input {
    border: none;
    border-bottom: 1px solid rgba(0, 0, 0, .12);
    display: block;
    font-size: 16px;
    font-family: "Helvetica", "Arial", sans-serif;
    margin: 0;
    padding: 4px 0;
    width: 100%;
    background: 0 0;
    text-align: left;
    color: inherit;
}
.tanukiButton:hover {
    color: #FFF;
    height: 36px;
    background: #FF5722;
}
.button:hover {
    color: #FFF;
    height: 36px;
    background: #FF5722;
}
/******************* KOLUMNER ***********************/ 
.pushColumn>.fluid>.inner>.mainContent {
    box-shadow: 0 2px 2px 0 rgba(0, 0, 0, .14), 0 3px 1px -2px rgba(0, 0, 0, .2), 0 1px 5px 0 rgba(0, 0, 0, .12);
    border-radius: 0px;
    border: none;
    background-color: #FFF;
}
.insiderColumn>.fixed>.inner {
    margin-right: 6px;
    margin-left: 16px;
    position: absolute;
    float: right;
    padding-left: 74px;
    width: 324px;
}
/******************* GALLERI-SIDAN ***********************/ 
.galleryPortal .galleryItem {
    box-shadow: 0 2px 2px 0 rgba(0, 0, 0, .14), 0 3px 1px -2px rgba(0, 0, 0, .2), 0 1px 5px 0 rgba(0, 0, 0, .12);
    background: white;
}
.galleryItem .image {
    border-radius: 0px;
}
.galleryItem .rating {
    color: #F4511E;
}
.hotGallery .rating {
    color: #F4511E;
    padding-top: 16px;
}
.hotGallery{
    background: white;
    box-shadow: 0 2px 2px 0 rgba(0, 0, 0, .14), 0 3px 1px -2px rgba(0, 0, 0, .2), 0 1px 5px 0 rgba(0, 0, 0, .12);
}

/******************* NYHETSSIDAN ***********************/ 
.newsList .commentsBubble{
        border-radius: 0;
}
.articleKeywords a {
    background-color: #dedede;
    color: rgba(0, 0, 0, .87);
    border-radius: 16px;
    border: 0;
    padding: 0 12px;
    display: inline-block;
    line-height: 32px;
}
.articleHeader h1 {
    color: #F4511E;
}
.articleHeader .meta {
    color: #F4511E;
}
.mainWidget,
.pushList,
.prisjaktPopular {
    box-shadow: 0 2px 2px 0 rgba(0, 0, 0, .14), 0 3px 1px -2px rgba(0, 0, 0, .2), 0 1px 5px 0 rgba(0, 0, 0, .12);
    border-radius: 0px;
}
.videoPush .image img {
    border-radius: 0px;
}
.videoPush .push,
.popularGalleries>.slide {
    color: #000;
    background: white;
    box-shadow: 0 2px 2px 0 rgba(0, 0, 0, .14), 0 3px 1px -2px rgba(0, 0, 0, .2), 0 1px 5px 0 rgba(0, 0, 0, .12);
}
.slide>.rating {
    color: #FF5722;
}
.videoPush h3 a {
    color: #000;
}
.sideBox{
    position: static;
    padding-left: 35px;
}
.sideBox h5{
    white-space: nowrap;
}
.popularGalleries .image {
    border-radius: 0;
}
/******************* PROFILSIDAN ***********************/ 
.profileHeader{  
	background: white;
    box-shadow: 0 2px 2px 0 rgba(0, 0, 0, .14), 0 3px 1px -2px rgba(0, 0, 0, .2), 0 1px 5px 0 rgba(0, 0, 0, .12);
    padding: 9px 14px 3px 10px;
}
.profileHeader .meta {
    color: #6a6a6a;
