/******************* FOOTER ***********************/ 
.footer .top {
    color: #F5F5F5;
    background: #9E9E9E;
    border-top: 1px solid #bab9b8;
    padding: 15px 0 22px;
}
.footer h6{
    color: #FFF;
}
.footer .bottom{
    background: #212121;
}
.sideShare, .articleByline {
    margin-left: 36px;
    margin-right: -36px;
    padding-left:0;
    padding-right: 10px;
    background:white;
    box-shadow: 0 2px 2px 0 rgba(0, 0, 0, .14), 0 3px 1px -2px rgba(0, 0, 0, .2), 0 1px 5px 0 rgba(0, 0, 0, .12);
}
.pushListExternal{
    background:white;
}
