css:
@-moz-document
	url-prefix('http://www.tumblr.com/'),
	url-prefix('https://www.tumblr.com/')
{


/* blue fix */
div.search_form_row {background-color: rgba(0,0,0,.6)}
div.l-header .selection_nipple {border-bottom-color: rgba(0,0,0,.6)}

.l-header-container--refresh {
    background-color: rgba(255,162,0,0.9) !important;
}

.identity-refresh .selected .tab_notice, .identity-refresh .inbox_notice_container, .identity-refresh .new_post_notice_container {
    background: rgba(255,162,0,1);
}

.compose-button {
    fill: #ffffff!important;

}

#left_column, #right_column {
	background-color: transparent;
}
.controls_section.user_list li.item .follow_list_item_blog:before {
  background-image: none;
  border-right: none;
  }
.radar div.radar_footer .radar_avatar:before {
  background-image: none;
  }
#right_column:after {display:none}

.blog.has_follow_button:before,
#right_column:after {display: none}

/* post editor */
.post_form_wrapper {
  background-color: rgba(240,170,0,.9);
  background-color: rgba(200,200,200,.5);
  border-radius: inherit !important;
  }

#post_header,
.main_content INPUT[type="text"],
.tag_editor { /* blog selector, tag editor */
  background: rgba(255,162,0,.6) !important;
  color: /*maroon*/black !important;
  }
#tumblelog_select > .txt {color: /*maroon*/black !important}
#post_header {border-radius: 6px 6px 0 0}

#create_post > .chrome.blue { /* Publish button */
  background: /*maroon*/black !important;
  color: rgb(255,162,0) !important;
  }

.plexi.color { /* "shield" behind the editor */
  background: /*rgba(85,54,0,.9)*/rgba(0,0,0,.8) !important;
  }

#new_post.post.editing .post_avatar:after { /* avatar nipple */
  border-right-color: rgb(255,162,0) !important;
  }

.post-forms-glass {
    display: block;
    position: fixed;
    top: 0;
    right: 0;
    bottom: 0;
    left: 0;
    width: 100%;
    height: 100%;
    z-index: 996;
    pointer-events: none;
    background-color: rgba(0,0,0,.95);
}

.blue#glass_overlay, .blue#hello_glass, .blue.glass {
    z-index: 160;
    transition: background-color 0.2s linear;
    background: rgba(0,0,0,.95);
}

/* background */
#new_post.post.editing,
BODY {
  background: url('http://i52.tinypic.com/2j5hv0j.jpg')
    fixed top black !important;
}
BODY.mceContentBody {background: white !important}

.l-content{
  background: rgba(0,0,0,.6) !important;

}

/* right column controls */
.controls_section A,
.controls_section .section_header,
.radar .radar_tumblelog_label > .name,
.radar .radar_tumblelog_label > .subheading {
  color: #ffa200 !important;
  }
.controls_section .section_header:not(:hover),
.radar .radar_tumblelog_label:not(:hover) > .name,
.radar .radar_tumblelog_label:not(:hover) > .subheading {
  color: rgba(255,162,0,.7) !important;
  }
.popover_blogs .popover_inner {
  background-color: rgba(160,100,0,1) !important;
  }
.popover_blogs .popover_menu_item:hover {
  background-color: rgba(255,255,255,.7) !important;
  }

#right_column:after {display:none}
  /* fuzzy blue thing at end of column */

.search_form_row {
	background-color: transparent !important;
	}
.search_query {
	color: black !important;
	}

/* static 'new post' icons */
/*.new_post_label_icon {background-image:none !important}*/
.new_post_label > I {display: none !important}
.new_post_label {color: transparent !important}
#new_post{
	background: url(http://i54.tinypic.com/2ymu906.jpg) center no-repeat #fff !important;
	}

.source_url, .tags {display: inline !important; white-space: normal !important;}
.source_url_gradient {display: none !important;}

/* pagination */
#auto_pagination_loader {
	background: none;
	border-top: 0px solid #1B2D42;
}
#pagination A {
	background-color: #ffa200 !important;
	color: maroon !important;
	opacity:.6 !important;
	}
#pagination A:hover {
	opacity:1 !important;
	}

#footer{
	background: rgba(10,10,10,0.6) !important;
}

/* clickable logo */

.logo .svg-logo {display: none}
.logo { /* logo dimensions */
	min-width: 237px;
	min-height: 47px;
}
.logo > .logo-anchor {
  display: block;
  height: 100%;
  background: url('http://i52.tinypic.com/2dl87jo.jpg') center/auto no-repeat;
  }

  }
