css:
@-moz-document domain("twitter.com"), regexp("http(s)://twitter.com/\\w+/media") {
.DashboardProfileCard-avatarLink,
.DashboardProfileCard-avatarImage,
.avatar {
    border-radius: 100% !important;
}
.import-prompt {
    display: none;
}
.home-tweet-box {
    position: absolute !important;
    margin: 190px 0 0 -302px;
    width: 266px;
}
#tweet-box-mini-home-profile {
    /*position:absolute !important;*/
    
    margin: 0 0 0 -60px;
    width: 250px;
}
.geo-picker,
geo.status {
    display: none;
}
.modal-tweet-form-container .tweet-form .photo-selector,
.tweet-form .geo-picker,
.tweet-form .lifeline-alert-status {
    margin-left: 60px
}
.tweet-form .photo-selector {
    position: relative;
    float: left;
    margin-left: -50px;
}
.tweet-form .thumbnail-container {
    position: absolute;
    margin-left: -60px;
    bottom: 4px;
    left: 4px;
    max-width: 280px;
    overflow: hidden
}
div[data-test-selector=ProfileTimeline] {
    width: 100% !important;
    font-size: 0;
}
div[data-component-term=tweet] {
    display: inline-block;
    width: 33%;
}
.ProfileTweet {
    border: none;
    border-top: 1px solid #e1e8ed
}
.GridTimeline-items {
    background-color: white;
}
div[data-component-term=tweet] .action-pocket-container,
div[data-component-term=tweet] .ProfileTweet-contextualLink {
    display: none;
}
div.ProfileTweet-action {
    margin-right: 20px;
}
.ProfileHeading-content {
    margin-bottom: 7px;
}
.Trends {
    display: none;
}
}
