css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("tr.instela.com") {

body {
    background-color: #ebebeb;
}

header.main_header {
    background-color: #383838;
    border-bottom: 0px;
}

header.main_header #top_buttons .floating_opener > a {
    border-right: 0px solid;
    border-left: 0px solid;
}

.fixed-title-header .logotipo a {
    background: url(https://s.instela.com/m/instela--i696380.jpg);
}
    
.fixed-title-header {
    background: #383838;
}

.fixed-title-header .featured a {
    padding: 11px 8px 10px 8px;
    color: #fff !important;
}
    
.right > ul > li, .entry_input, .inner_page, .white_section {
    border: 0px;
}

.alternative-header-lamps li > a {
    color: #fff !important;
}

.alternative-header-lamps li > a:hover {
    color: #fff !important;
}

.alternative-header-lamps li.sahsim .floating a:hover {
    color: #fff !important;
}

.panelmenu .content > ul > li > a:hover {
    background-color: #383838;
}

.panelmenu .content > ul > li > a {
    color:#000;
}
    
#notification-popup > ul, .panel.notifications > ul, .top-popup #display_all, .floating_opener.open .floating, .floating_opener_hover:hover .floating, form.report select, form.report textarea, form.delete-entry select, form.delete-entry textarea, .send-message > div > textarea {
    background-color: #ebebeb;
}
    
header.main_header .floating_opener.open > a, header.main_header .floating_opener.floating_opener_hover:hover > a {
    background-color: #ebebeb !important;
}

a {
    text-decoration: none !important;
}

h1 a, h2 a, h3 a {
    color: #383838 !important;
    text-decoration: none !important;
}

h1 a {
    color: #383838 !important;
    text-decoration: none !important;
}
    
h2 a {
    color: #383838 !important;
    text-decoration: none !important;
}

h2 a:hover {
    color: #383838 !important; 
    text-decoration: none !important;
}
    
.clairvoyance {
    background-color: transparent;
}
  
.entries > article {
    position: relative;
    background-color: transparent;
}
    
.entries > article h2 {
    color: #383838 !important;
    text-decoration: none !important;
}

.entries .entry_header .dateauthor > a.hop {
    color: #383838 !important;
    text-decoration: none !important;
}

.entries footer {
    border-bottom: 3px solid #e2e2e2;
    border-top: 0px solid #e5e5e5;
}
    
.new-entry-input {
    background-color: #ebebeb;
}
    
.new-entry-input.focus textarea, .entry-comments form .comment-table > div textarea {
    font-size: 13px;
    background-color: #ebebeb;
}
  
nav#bgleft > * {
    background-color: transparent;
}

#bgleft a {
    text-decoration: none !important;
    color: #383838;
}
    
#bgleft .hd {
    border: 0px; 
}
    
#bgleft > ul {
    border-bottom: 0px;
    border-top: 0px;
    border-left: 0px;
    border-right: 0px;
}
    
#bgleft .mode-select {
    border: 0px;
    background-color: transparent;
}

#bgleft .mode-select > ul > li > a:hover {
    color: #fff;
    text-decoration: none !important;
    background-color: #383838;
    border-radius: 3px;
}

#bgleft .mode-select .floating > li > a:hover {
    background-color: #383838;
}
    
body.no-touch #solframe li:hover {
    border-left:4px solid #383838;
}
    
#bgleft #solframe {
   font-size: 14px;
}
    
#bgleft .hd:last-child {
    border-bottom: 0px;
}
    
#solframe li span.title-section {
    color: #383838;
}
 
#solframe.mode-mostpopular li span.title-section  {
    font-size: 14px;
}
    
#bgleft #solframe li > div, #bgleft #solframe li > a:hover, body.no-touch #bgleft #solframe li:hover, body.no-touch #bgleft #solframe li.selected, body.no-touch #bgleft #solframe a.x {
    background-color: #ebebeb;
}

#solframe.mode-mostpopular li {
    font-size: 13px;
    background: transparent;
    border: 0px;
    border-bottom: 1px solid #e2e2e2;
}
    
.related-titles-right {
    background-color: #ebebeb;
    border: 0px;
}
    
.right-box.invite-to-instela form > :first-child > * {
    background-color: #ebebeb;
}
    
.right-section > .right-box, .right-section > .right-box:hover {
    background-color: transparent;
    border: 0px;
}
    
.right-section a {
    color: #383838;
    font-size: 13px; 
}
    
.right-box.duyurular .duyuru a:hover, .right-box.duyurular .related a:hover, .right-box.duyurular .top20 a:hover, .right-box.related .duyuru a:hover, .right-box.related .related a:hover, .right-box.related .top20 a:hover {
    background-color: #ebebeb;
}
    
.related-titles-right {
    border: 0px;
}

.related-titles-right .badge {
    background: #383838;
    color: #fff;
}
    
.related-titles-right .related-link:first-child {
    padding-top: 1px;
}

.related-titles-right .related-link {
    padding: 3px 0;
}

.related-titles-right .related-link h3 {
    margin-left: 10px;
    margin-bottom: 0;
    margin-right: 10px;
    font-size: 14px;
}

.right-box.duyurular .duyuru a, .right-box.duyurular .related a, .right-box.duyurular .top20 a, .right-box.related .duyuru a, .right-box.related .related a, .right-box.related .top20 a {
    display: block;
    border-bottom: 1px solid #e2e2e2;
    text-align: left;
    padding: 3px 10px 3px 10px;
    color: #000;
    font-size: 14px;
}
    
.gosterpager a, .gosterpager select {
    color: #383838;
    background-color: #efefef;
}

body.no-touch .gosterpager a:hover {
    background-color: #383838;
}
    
.entries_section.title_header {
    background-color: transparent;
    border-bottom: 3px solid #e2e2e2;
}
    
.entries_section {
    background: #ebebeb;
}
    
.embedded-context {
    background-color: #ebebeb;
    margin-top: 30px;
}
    
.entries > article .entry-resume {
    background: #ebebeb;
    
}
.embedded-context {
    background-color: #ebebeb;
    margin-top:30px;
}
    
.entries > article {
    border: 0px;
}
    
.entry-tags > .entry-tag .tag-link {
    background-color: #ebebeb;
    color: #5b5b5b;
}
    
.new-entry-input:not(.focus) {
  border: 1px solid #cfcfcf;
  font-size: 13px;
}
    
article.entry-object.edit-enable .edit-entry textarea {
    background-color: #ebebeb;
    font-size: 13px;
}
    
.entries .entry_text {
  font-size: 13px;
}
    
.entry-comments {
    background-color: #ebebeb;
}
    
form.post-entry-box {
    background-color: #ebebeb;
}
    
.embed-link > span {
    background-color: #dbdbdb;
    border: 3px solid #e2e2e2;
}
    
.embed-link .link-source {
    font-size: 14px;
    color: #383838;
    margin-top: -12px;
}

.embed-link .link-title {
    font-size: 14px;
    color: #383838;
}

.embed-link.with-image .link-content {
    min-height: 0px;
    background-color: #ebebeb;
}

.embed-link .link-description {
    display: none;
}

.embed-link img {
    display: none;
}

.sf_section > span.news, span.new-count {
    background-color: #383838;
}

body.no-touch .featured .sf_desc a::after, body.no-touch .featured a::after {
    background: #383838;
    height: 2px;
}
    
.index-header {
    border: 0px;
    background-color: transparent;
}

.index-header .selector li > a::after {
    background: #383838;
    height: 2px;
}

.new-entry-input:not(.focus) {
    display: none;
}
    
.view-all-page.load-more-fav {
    border: solid 1px #cecece;
    background-color: transparent;
}
    
.view-all-page {
    background-color: #ebebeb;
    font-size: 14px;
}

input[type="button"], input[type="submit"], .but {
    color: #fff !important;
    background-color: #383838;
} 
    
.panel > section {
    background-color: transparent;
    border: 0px;
}
    
.index-header .selector li > a:after  {
    background:none repeat scroll 0% 0% #383838;
}

.selector li:hover a {
    color:#fff !important;
}

.index-header .selector li > a {
    color: #000 !important;
}
    
.panel.friend-finder > section {
    background-color: #ebebeb;
    border: 0px;
}

.stargate > form {
    background-color: #ebebeb;
}
    
.profile > .profile_header, .profile > .profile_header > .profile_stats {
    border: 0px;
    background-color: transparent;
}

.profile > .profile_header a:hover {
    color: #383838;
}
    
.profile > .profile_header .avatar-container #avatar-change span {
    display: block;
    margin: 10px 10px;
}
    
.profile > .profile_header .avatar {
    background: #ebebeb;
    border: 1px solid #d7d7d7;
}
    
.panel.messages #messagesContainer > .conversations, .message-popup.messages #messagesContainer > .conversations, .panel.messages #messagesContainer, .message-popup.messages #messagesContainer, .panel.messages #messagesContainer > .conversations .conversation .message_header, .message-popup.messages #messagesContainer > .conversations .conversation .message_header, .panel.messages #messagesContainer > .threads > form > div > div input, .message-popup.messages #messagesContainer > .threads > form > div > div input, .panel.messages #messagesContainer > .conversations .conversation form .message_form, .message-popup.messages #messagesContainer > .conversations .conversation form .message_form, .panel.messages #messagesContainer > .conversations .conversation form .message_form textarea, .message-popup.messages #messagesContainer > .conversations .conversation form .message_form textarea, .panel.messages #messagesContainer > .threads #thread_list > a:hover {
    border-left: 0px;
    border: 0px;
    background-color: #ebebeb;
}
    
.date-seperator {
    color: #383838;
    border-top: 1px solid #d7d7d7;
}
.date-seperator span {
    background-color: #d7d7d7;
    padding: 0 5px;
    position: relative;
    top: -12px;
}
  
.right-section footer.dip {
    display: none;
}

footer.dip {
    border-top: 15px solid #383838;
    background: #ebebeb;
}

}
