css:
@-moz-document domain("weibo.com") {
/*元素隐藏*/

/* 搜索推荐热搜榜 */
div.gn_topmenulist > div[node-type="plus"] > ul:nth-child(2){
    display: none !important;
}

/* 顶部导航 */
.WB_global_nav_v2 .gn_nav_list li:nth-child(1),
.WB_global_nav_v2 .gn_nav_list li:nth-child(2),
.WB_global_nav_v2 .gn_nav_list li:nth-child(3),
.WB_global_nav_v2 .gn_nav_list li:nth-child(4){
    display: none !important;
}

/* 私信聊天浮框 */
#WB_webim {
    display: none !important;
}

/* 音乐播放器 */
.PCD_mplayer {
    display: none !important;
}

/* 微博背景 */
.WB_starcover {
    display: none !important;
}

.WB_feed_detail {
    background: none!important;
}

/* 收藏按钮和点赞按钮 */
.feed_action_info.feed_action_row4 > li:nth-child(1),
.feed_action_info.feed_action_row4 > li:nth-child(4),
.WB_row_line.WB_row_r4.clearfix.S_line2 > li:nth-child(1),
.WB_row_line.WB_row_r4.clearfix.S_line2 > li:nth-child(4) {
    display: none !important;
}

.WB_row_line.WB_row_r3.clearfix.S_line2 > li:nth-child(3) {
    display: none !important;
}

.WB_handle.W_fr > ul > li:nth-child(3){
    display: none !important;
}

.layer_multipic_preview .WB_func .WB_handle li:nth-child(1){
    display: none !important; 
}

/* 图片点赞 */
.artwork_box .W_btn_alpha ,.pos_margin{
    display: none !important;
}

/* 大图底部预览 */
.pic_choose_box.carousel{
    display: none !important;
}

/* 微博等级图标 */
.icon_member1,
.icon_member2,
.icon_member3,
.icon_member4,
.icon_member5,
.icon_member6,
.icon_member_dis{
    display: none !important;
}

/* 微博气球图标 */
.icon_airball {
    display: none !important;
}

/* 微博右侧下拉 */
.WB_screen.W_fr {
    display: none !important;
}

/* 下侧功能 */
.WB_tag_rec {
    display: none !important;
}

/* 隐藏XX等人赞过的博文 */
#v6_pl_content_homefeed .WB_feed .WB_cardwrap:nth-of-type(n+2) .WB_cardtitle_b { background-color: #fafafa !important; margin-top: -11px !important; padding: 1px  0 0 0 !important; height: 0px !important; }
#v6_pl_content_homefeed .WB_feed .WB_cardwrap:nth-of-type(n+2) .WB_cardtitle_b  ~ div, .WB_cardwrap:nth-of-type(n+2) .WB_cardtitle_b h4 { display: none !important; }

/* 返回顶部 */
.W_gotop {
    display: none !important;
}

/* 搜索默认关键词 */
.placeholder {
    display: none !important;
}

/* 微博等级 */
[node-type="levelBox"] ,.W_icon_level {
    display: none !important;
}

/* 推荐阅读 */
[node-type="recommend"]{
    display: none !important;
}

/* 微博达人 */
.icon_club{
    display: none !important;
}

/* 多重转发提示 */
.wft_hd{
    display: none !important;
}

/* ad */
.W_tips.tips_warn.clearfix{
    display: none !important;
}

.W_btn_alpha_v2{
    display: none !important;
}

/*预览卡片*/
.WB_feed_spec{
    display: none !important;
}



/* ---------------------只在首页应用---------------- */

/* 隐藏标签 */
.tab.W_fl.clearfix > li.S_bg2 > a.S_txt1 {
    display: none !important;
}

/* 左侧导航 */
#v6_pl_leftnav_group {
    display: none !important;
}

#v6_pl_content_setskin,
#v6_pl_rightmod_myinfo,
#v6_pl_rightmod_rank,
#v6_pl_rightmod_recominfo,
#v6_pl_rightmod_ads35,
#v6_pl_rightmod_attfeed,
#v6_pl_rightmod_ads36,
#v6_trustPagelet_recom_member,
#v6_pl_rightmod_noticeboard,
#v6_pl_ad_bigday,
#v6_pl_ad_forfqy{
        display: none !important;
}


/* 叠加背景 */
.WB_frame {
    background: none!important;
}

/* 微博发布框 */
.send_weibo.S_bg2 {
    display: none !important;
}

/* footer移除 */
.WB_footer.S_bg2{
    display: none !important;
}

/* ---------------------只在用户详情页应用---------------- */


/* 详情页右侧功能 */
#Pl_Third_Inline__4,
#Pl_Core_Ut2UserList__5,
#Pl_Core_P7MultiPicPlay__6,
#Pl_Core_PicTextList__8,
#Pl_Core_PicText__9,
#Pl_Core_Ticket__10,
#Pl_Core_PicTextMixed__11,
#Pl_Core_Pt13PicText__12,
#Pl_Core_PicText__13,
#Pl_Core_P6Video__14,
#Pl_Core_Ut1UserList__15,
#Pl_Core_Ut1UserList__16,
#Pl_Core_Ut1UserList__17,
#Pl_Third_Inline__18,
#Pl_Official_SeoInfo__20,
#Pl_Third_Inline__22,
#Pl_Core_FansGroups__11,
#Pl_Core_P6Video__18,
#Pl_Core_P7MultiPicPlay__6,
#Pl_Core_P7MultiPicPlay__7,
#Pl_Core_PicText__10,
#Pl_Core_PicText__14,
#Pl_Core_PicText__16,
#Pl_Core_PicText__17,
#Pl_Core_PicTextList__10,
#Pl_Core_PicTextList__11,
#Pl_Core_PicTextList__9,
#Pl_Core_PicTextMixed__12,
#Pl_Core_Pt13PicText__13,
#Pl_Core_Pt13PicText__8,
#Pl_Core_Pt6Rank__13,
#Pl_Core_Pt6Rank__15,
#Pl_Core_Pt6Rank__15,
#Pl_Core_Pt6Rank__16,
#Pl_Core_ThirdHtmlData__4,
#Pl_Core_Ticket__11,
#Pl_Core_UserGrid__17,
#Pl_Core_Ut1UserList__18,
#Pl_Core_Ut1UserList__19,
#Pl_Core_Ut1UserList__19,
#Pl_Core_Ut1UserList__20,
#Pl_Core_Ut1UserList__21
#Pl_Core_Ut2UserList__5,
#Pl_Core_Ut2UserList__6,
#Pl_Guide_Bigday__22,
#Pl_Guide_Bubbletip__21,
#Pl_Official_LikeMerge__19,
#Pl_Official_MyPopularity__20,
#Pl_Official_SeoInfo__23,
#Pl_Third_Inline__13,
#Pl_Third_Inline__14,
#Pl_Third_Inline__5,
#Pl_Third_Inline__9,
#Pl_Official_LikeMerge__20{
    display: none !important;
}

/* 热门标签 */
[node-type="tab_hot"] {
    display: none !important;
}

/* ---------------------只在微博详情页应用---------------- */
#Pl_Core_RecommendList__58，
#Pl_Core_RecommendList__78{
    display: none !important;
}
}

@-moz-document domain("weibo.com") {
/*功能调整*/

/* 背景相关调整 */
body{
	background-image:none !important;
    background-color: rgb(0, 0, 0) !important;
}

.WB_miniblog{
	background-image:none !important;
}

.cover_wrap.banner_transition{
	background-image:none !important;
}

/* 单条微博底部功能宽度 */
.feed_action_info.feed_action_row4 > li,
.WB_row_r4 li {
    width: 50%;
}

/* 取消圆形头像 */
.W_face_radius ,
.PCD_header .pf_photo{
    border-radius: 10%;
}

/*秒拍卡片*/
.WB_video {
    width: 110px!important;
    height: 110px!important;
    margin: 4px 0 0 4px;
}

/*统一图片尺寸*/
.WB_pic.li_1.S_bg1.S_line2.bigcursor{
    width: 110px!important;
    height: 110px!important;
    margin: 4px 0 0 4px;
}

