.WB_global_nav{
	position: relative;
}

.W_fixed_top{
	position: absolute !important;
}

.WB_miniblog {
    padding-top: 0px !important;
}

.PCD_header .photo_wrap, .PCD_header .pf_photo .photo {
    border-radius: 10%;
}

/* 个人页面 轮播*/
#Pl_Core_P7MultiPicPlay__21{
	display: none !important;
}

/*个人页面 封面图相关功能*/
.pf_use_num,.pf_copy_icon{
	display: none !important;
}

/*个人页面整体布局*/
