}

#plc_main > .WB_frame_b{
    position: relative;
    left: 730px;
}*/

.WB_timeline{
	display: none !important;
}

/*.WB_feed_v3 .WB_expand .WB_text[node-type="feed_list_reason"]{
	display: block !important;
}

.WB_feed_v3 .WB_expand .WB_text[node-type="feed_list_reason_full"]{
	display: block !important;
}

.WB_text .WB_text_opt {
    display: none;
}*/

/* 用户详情页和微博详情页布局*/
.B_page .WB_frame #plc_main > .WB_frame_b,
.B_page .WB_frame #plc_main > .WB_frame_c{
    position: relative;
    right: -160px;
}

.B_page .WB_frame #plc_main .WB_frame_b{
	float: right;
}

.PCD_tab.S_bg2{
    width: 600px;
    margin-right: auto;
    margin-left: auto;
}

.WB_frame #plc_main > .WB_main_c{
    position: relative;
    right: -50px;
}

.shadow.S_shadow{
	background:none;
}

.WB_cardwrap.WB_feed_type.S_bg2 {
    box-shadow: 0px 3px 7px rgba(0, 0, 0, 0.12);
}

.W_icon.icon_setskin.UI_top_hidden.W_fixed_top{
    display: none !important;
}
}

@-moz-document domain("weibo.com") {
/* skin */

.S_spetxt,
a.S_ficon:hover,
a:hover .S_ficon,
a.current .S_ficon {
    color: #fa7d3c;
}
.S_error {
    color: #f00;
}
.S_spetxt_bg {
    background-color: #fa7d3c;
}
.W_btn_prev:hover,
.W_btn_next:hover {
    border-color: #fa7d3c;
}
.W_btn_prev:hover,
.W_btn_next:hover,
a.S_ficon:hover,
a:hover .S_ficon,
a.current .S_ficon,
a.S_txt1:hover,
a.current .S_txt1,
a.S_txt2:hover,
.SW_fun:hover .S_func1 {
    text-decoration: none;
    cursor: pointer;
}
.S_ficon_dis,
a.S_ficon_dis:hover,
a:hover .S_ficon_dis {
    cursor: default;
    opacity: 0.5;
    -moz-opacity: 0.5;
    filter: alpha(opacity=50);
}
.WB_timeline .current em {
    color: #fff;
}
.send_weibo {
    background-image: none;
}
.WB_miniblog {
    padding-top: 50px;
}
.S_page .WB_miniblog {
    padding-top: 50px;
}
.S_page .WB_frame {
    background-color: transparent!important;
}
#js_skin_public_base {
    height: 42px;
}
body,
.S_page {
    background-color: rgb(100, 100, 100) !important;
    background-image: none;
}
.send_weibo .ficon_swtxt {
    color: #4179c5;
}
body,
legend,
.W_input:focus,
.S_txt1,
.W_btn_b,
.SW_fun .S_func1 {
    color: #333;
    text-decoration: none;
}
.S_txt1_bg {
    background-color: #333;
}
.S_txt1_br {
    border-color: #333;
}
.S_txt2,
.W_input,
.W_btn_b_disable,
.W_btn_b_disable:hover {
    color: #808080;
    text-decoration: none;
}
.S_txt2_bg {
    background-color: #808080;
}
.S_txt2_br {
    border-color: #808080;
}
.S_ficon,
.S_ficon_dis,
a.S_ficon_dis:hover,
a:hover .S_ficon_dis {
    color: #696e78;
}
.S_ficon_bg {
    background-color: #696e78;
}
.S_ficon_br {
    border-color: #696e78;
}
a,
.S_link1,
a.S_txt1:hover,
a.current .S_txt1,
a.S_txt2:hover,
.SW_fun:hover .S_func1 {
    color: #3870a9;
}
.S_link1_bg {
    background-color: #3870a9;
}
.S_link1_br {
    border-color: #3870a9;
}
.S_bg1,
.SW_fun_bg:hover,
.SW_fun_bg_active {
    background-color: #f2f2f5;
}
.S_bg1_c {
    color: #f2f2f5;
}
.S_bg1_br {
    border-color: #f2f2f5;
}
.W_btn_cardlink,
.W_btn_tag_cur,
.W_btn_tag_cur:hover {
    background-color: #f2f2f5!important;
}
.S_bg2,
blockquote,
.W_btn_b,
.W_input,
.SW_fun_bg {
    background-color: #fff;
}
.S_bg2_c {
    color: #fff;
}
.S_bg2_br {
    color: #fff;
    border-color: #fff;
}
.S_line1,
.W_btn_prev,
.W_btn_next,
.W_btn_b {
    border-color: #d9d9d9;
}
.W_btn_b_disable,
.W_btn_b_disable:hover,
.W_btn_tag_cur,
.W_btn_tag_cur:hover {
    border-color: #d9d9d9!important;
}
.S_line1_c {
    color: #d9d9d9;
}
.W_btn_b_disable .S_ficon {
    color: #d9d9d9!important;
}
.S_line2 {
    border-color: #f2f2f5;
}
.S_line2_c {
    color: #f2f2f5;
}
.S_line3,
.W_input,
.send_weibo .input,
.W_btn_b:hover {
    border-color: #ccc;
}
.W_input,
.send_weibo .input {
    background-color: #fff;
}
.WB_left_nav .S_txt1,
.WB_left_nav a.S_txt1:hover,
.WB_left_nav .S_ficon,
.WB_left_nav a:hover .S_ficon,
.WB_left_nav a.S_ficon:hover {
    color: #fff;
}
.WB_left_nav .lev a:hover,
.WB_left_nav .lev_curr,
.WB_left_nav .lev_curr:hover,
.WB_left_nav .levmore .more {
    background-color: #4e4e4e;
    background-color: rgba(255, 255, 255, 0.2);
    *background-color: #4e4e4e;
}
.WB_left_nav .lev_Box,
.WB_left_nav fieldset {
    border-color: #4e4e4e;
    border-color: rgba(255, 255, 255, 0.1);
    *border-color: #4e4e4e;
}
.WB_frame {
    background-color: #222222;
    background-color: rgba(73, 72, 72, 0.25);
    *background-color: #222222;
}
.WB_timeline .S {
    color: #3870a9;
    border-left-color: #3870a9;
}
.WB_timeline .current em,
.WB_timeline .S_dot,
.W_gotop {
    background-color: #3870a9;
}
.WB_miniblog {
    background: none;
}
.S_page .WB_miniblog {
    background: none;
}
}

@-moz-document domain("s.weibo.com") {
/* 背景 */
.S_wrap{
	background-image:none;
}

/* 右侧功能 */
.S_content_r{
	display:none;
}
