css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("addons.mozilla.org") {
  html, body { color: #777 !important; background-color: #000C17 !important; }
  #background-wrapper, #main-wrapper { background-color: #000C17 !important; }
  
  input { color: #777 !important; }
  input[type="text"], textarea, select { background-color: #000C17 !important; border: 1px solid #00ACAF !important; color: #777 !important; }
  /* add button */
  a.button.add:link { box-shadow: none !important; background-color: #002200 !important; }
  a.button.add:hover { box-shadow: none !important; background-color: #005500 !important; }
  
  /* header bg */
  .header-bg { background-color: #000C27 !important; }
  .home .header-bg, .header-bg {
    background-image: repeating-linear-gradient(118deg,rgba(12,153,213,0) 0,#000C17 550px,rgba(12,153,213,0) 550px,#000C27 800px,rgba(12,153,213,0) 800px,#000C37 950px)
  }
  
  /* top right menu(s) */
  .amo-header ul a { color: #00ACAF !important; }
  .menu-nav > ul > li > ul { background-color: #000C67 !important; } /* bg color */
  .menu-nav > ul > li > ul::after { background-color: #000C67 !important; } /* arrow color */
  .menu-nav > ul > li::after { background-color: #000C67 !important; } /* silly top bar in menu */
  .menu-nav > ul > li > ul a { color: #00ACAF !important; } /* text color */
  .menu-nav > ul > li > ul a:hover { background-color: #000C27 !important; } /* menu item hover */
  
  .menu-nav em { background-color: #000C47 !important; }
  #site-nav a.complete-themes { background-color: #000C47 !important; }
  #site-nav a.complete-themes { border-top-color: #00ACAF !important; }
  .menu-nav hr { border-top-color: #00ACAF !important; }
  
  /* search box */
  input#search-q { border-color: transparent !important }
  .header-search input { background-color: #000C67 !important; } /* search box bg */
  #site-search-suggestions .wrap { background-color: #000C67 !important; border-color: #00ACAF !important; box-shadow: none !important; } /* suggestion main bubble */
  #site-search-suggestions a.sel { background-color: transparent !important; } /* suggestion text bg - why? */
  .header-search .search-button { background-color: #000C87 !important; }
  
  /* 1sg page content */
  .primary, .secondary { background-color: #000C27 !important; } /* main content bg */
  header h1, .primary h2, h3, h4 { color: #00ACAF !important; } /* center headers */
  .secondary h2 { color: #00ACAF !important; } /* left bar headers */
  
  /* hover cards */
  .hovercard:hover,
  .hovercard.addon:hover,
  .hovercard:hover .more,
  .hovercard.collection:hover .more, 
  .hovercard.addon:hover .summary,
  .hovercard.addon:hover .icon {
    background-color: #000C67 !important;
    border-color: #00ACAF !important;
  }
  .hovercard .desc, .hovercard .more { color: #777 !important; } /* hover card text */
  
  /* left bar */
  .secondary li a.selected, .secondary li a:hover { background-color: #000C67 !important; }
  
  #footer {
    background-color: #000C27 !important;
    background-image: linear-gradient(to bottom,#000C37 0,#000C37 123px,#000C67 123px,#000C67 200px,#000C37 200px,rgba(12,12,12,0) 400px)
  } /* */
  
  /* ======== addon pages ======== */
  
  .addon-details .header-bg {
    background-image: repeating-linear-gradient(118deg,rgba(12,153,213,0) 0,#000C17 550px,rgba(12,153,213,0) 550px,#000C27 800px,rgba(12,153,213,0) 800px,#000C37 950px)
  }
  .restyle .previews { background-color: #000C47 !important; }
  .restyle .previews .control.prev { background: linear-gradient(to right,#000C17 50%,rgba(245,245,245,0)) !important; }
  .restyle .previews .control.next { background: linear-gradient(to left,#000C17 50%,rgba(245,245,245,0)) !important; }
  
  /* desc */
  .restye .primary.island, .primary.island { border-color: #00ACAF !important; }
  .prose { color: #777 !important; }
  
  /* reviews */
  #reviews .review h3, #reviews .review h4 { color: #00AACF !important; }
  #reviews .review .description { color: #777 !important; }
  
  /* ======== theme pages ======== */
  hgroup h2.addon { color: #00ACAF !important; }
  .object-lead { background: #000C37 !important; }
  .article { background-color: #000C37 !important; border-color: #00ACAF !important; }
  
  /* add review */
  .highlight, .modal { background-color: #000C37 !important; color: #777 !important; } /* add review box ??? */
  .ratingwidget, .stars { background-color: #000C87 !important; }
  input[type="submit"]:not(.upvotes):not(.downvotes), a.button:link, .button { background: #000C17 none !important; box-shadow: none !important; color: #00ACAF !important; border: 1px solid #00ACAF !important; }
  input[type="submit"]:not(.upvotes):not(.downvotes):hover, a.button:hover, .button:hover { background-color: #000C67 !important; }
}
