css:
@-moz-document url-prefix("http://runouw.com/forums") {
/* Reset backgrounds */
span.corners-top, span.corners-bottom, span.corners-top span, span.corners-bottom span, .forabg, .forumbg, .bg1, .bg2, ul.forums,
li.row:hover, #tabs a {
	background: none !important;
}

body {
    background-image: url(http://i.imgur.com/WAlETDH.png) !important;
    background-repeat: no-repeat;
    background-attachment: fixed;
    background-position: center; 
}


/* Background Colours */
.forabg, .forumbg, .navbar2 {
	background: rgba(40,0,60,0.5) !important;
}

.navbar {
	background: rgba(10,0,60,0.5) !important;
}

.headerbar {
	background: rgba(40,0,60,0.5) !important;
}

.rules {
    background-color: rgba(40,0,60,0.5) !important;
}

#navigation a {
	background: linear-gradient(to right, rgba(96,96,96,0.4) 0%, rgba(112,112,112,0.3) 100%) !important;
}

#navigation a:hover, #navigation #active-subsection a {
	background: linear-gradient(to right, rgba(64,64,64,0.4) 0%, rgba(80,80,80,0.3) 100%) !important;
}

html, body, select, input.search, .inputbox, .pagination span a, .pagination span a:link, .pagination span a:visited,
dl.codebox {
	background-color: #FFF !important;
}
	
.bg1, ul.forums, .panel, #tabs a {
	background-color: rgba(10,5,60,0.5) !important;
}

.bg2 {
	background-color: rgba(10,5,80,0.5) !important;
}

.bg3 {
    background-color: rgba(0,0,0,0) !important;
}

li.row:hover {
	background-color: rgba(40,0,180,0.5) !important;
}

blockquote {
	background-color: rgba(40,0,180,0.3) !important;
}

.pagination span a:hover, .pagination span strong {
	background-color: #888 !important;
}

.pagination span strong {
	background-color: #a96 !important;
}

.postlink:hover {
	background-color: #ddd !important;
}

#tabs a span, #tabs a {
	background: inherit;
    background-color: rgba(0,0,0,0) !important;
}

dl.codebox {
    background-color: rgba(40,0,180,0.3) !important;
}


/* Text Colours */
#page-body p {
    color: #fff !important;
}

.forumtitle, .topictitle, h2, h2 a, h3 a,
h2 a:link, h2 a:visited, .pagination a:link, .pagination a:visited, a.left-box:link, a.left-box:visited, a.right-box:link, a.right-box:visited {
	color: rgba(110,90,240,1) !important;
}

blockquote {
    color: #ddd !important;
}

#tabs li.activetab a span, #navigation li#active-subsection a span {
    color: rgba(110,90,240,1) !important;
}

#tabs li:not(.activetab) a span, #navigation li:not(#active-subsection) a span {
    color: #ddd !important;
}

#tabs li:not(.activetab) a:hover span, #navigation li:not(#active-subsection) a:hover span {
    color: rgba(70,50,200,1) !important;
}

code {
    color: rgba(110,90,240,1) !important;
}


/* Scrollbar */
::-webkit-scrollbar {
    background-color: rgba(27,67,105,0.9);
    width: 12px;
    height: 12px;
}

::-webkit-scrollbar-thumb {
    background-color: #203276;
    border-top-left-radius: 99px;
    border-top-right-radius: 99px;
    border-bottom-right-radius: 99px;
    border-bottom-left-radius: 99px;
    -webkit-box-shadow: inset 0 0 2px rgba(0,0,0,0.5);
}

::-webkit-scrollbar-track {
    -webkit-box-shadow: inset 0 0 6px rgba(27,87,125,0.3); 
    border-radius: 10px;
}

::-webkit-scrollbar-corner, ::-webkit-resizer {
    background-color: rgba(27,67,105,0.9);
}
}
