css:
@-moz-document regexp("https://pve.*vs.*:.*/.*"), regexp("https://proxmox.*vs.*:.*/.*"), regexp("https://.*:8006/.*") {
html, body
  {
    background-color: #222 !important;
    background-image: url(data:image/png;base64,) !important;
    background-clip: border-box !important;
    background-origin: padding-box !important;
    background-repeat: repeat !important;
    background-size: auto !important;
    background-position: left top !important;
    background-attachment: scroll !important;
    color: #fff !important;
  }

.x-grid-view
  {
    background-color: #222 !important;
    color: white;
  }

.x-tree-view
  {
    background-color: #222 !important;
    color: white;
  }

.x-fit-item
  {
    background-color: #222 !important;
    color: white;
  }

.x-unselectable
  {
    background-color: #222 !important;
    background-image: url(data:image/png;base64,) !important;
    background-clip: border-box !important;
    background-origin: padding-box !important;
    background-repeat: repeat !important;
    background-size: auto !important;
    background-position: left top !important;
    background-attachment: scroll !important;
    color: white;
  }

.x-box-inner
  {
    background-color: #222 !important;
    background-image: url(data:image/png;base64,) !important;
    background-clip: border-box !important;
    background-origin: padding-box !important;
    background-repeat: repeat !important;
    background-size: auto !important;
    background-position: left top !important;
    background-attachment: scroll !important;
    color: white;
  }

div, dl, dt, dd, ul, ol, li, h1, h2, h3, h4, h5, h6, pre, code, form, fieldset, legend, p, blockquote, th, td
  {
    color: lightblue;
  }

input, textarea
  {
    background-color: gray !important;
    color: white;
  }

.x-btn .x-btn-center .x-btn-inner
  {
    color: brown;
  }

.x-menu-item-text
  {
    color: brown;
  }

.x-toolbar .x-toolbar-item
  {
    background-color: #222 !important;
    color: green;
  }

.x-form-item-label
  {
    background-color: #222 !important;
    color: white;
  }

.x-panel-body
  {
    background-color: #222 !important;
    background-image: url(data:image/png;base64,) !important;
    background-clip: border-box !important;
    background-origin: padding-box !important;
    background-repeat: repeat !important;
    background-size: auto !important;
    background-position: left top !important;
    background-attachment: scroll !important;
    color: white;
  }

.x-tab-inner-default
  {
    background-color: #222 !important;
    color: gray;
  }

.x-tab.x-tab-active.x-tab-default .x-tab-inner-default
  {
    background-color: #222 !important;
    color: white;
  }

.x-btn-inner-default-toolbar-small
  {
    background-color: #222 !important;
    color: coral;
  }

.noVNC_status_normal
  {
    background-color: #222 !important;
    background-image: url(data:image/png;base64,) !important;
    background-clip: border-box !important;
    background-origin: padding-box !important;
    background-repeat: repeat !important;
    background-size: auto !important;
    background-position: left top !important;
    background-attachment: scroll !important;
    color: white;
  }

#noVNC_status
  {
    background-color: #222 !important;
    color: coral;
  }

}
