css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("pennergame.de"),
domain("pennergame.de"),
domain("pennerhilfe.de"),
domain("brands4friends.de") {
   
    /* applies font to all generic HTML tags */
    a,
    body,
    br,
    button,
    div,
    font,
    h1,
    h2,
    h3,
    h4,
    h5,
    h6,
    html,
    iframe,
    input,
    label,
    option,
    p,
    select,
    span,
    table,
    tr  {
        font-family: 'Lucida Grande', Arial, Verdana, sans-serif !important;
        font-size: 9px !important;
    }
    td {
        font-family: 'Lucida Grande', Verdana, Arial, Helvetica, sans-serif !important;
        font-size: 9px !important;
    }

     /* removes link underlining */
    a {
        text-decoration: none !important;
    }
    a:hover {
    color:#0069B3 !important;
    }
    /*body {
    background-image:url(http://www.4shizzl.de/stuff/bg.jpg) !important;
    }*/

    #robinson { display:none; }

}
