css:
@-moz-document regexp("(^https?://((blog|api|lib|manual|code).)?haxe.org.*)") {
/* haxe.org & general things */
nav .dropdown-menu {
	background-color: #181818;
}

table.gssb_c {
	color: #1e1e1e;
}

.dropdown-menu>li>a {
 	  color: #b8b5b5;
}

.dropdown-menu>li>a:hover {
 	  color: #fff;
}

::selection {
    color: #eee;
    background-color: #aaa;   
}

nav .dropdown-menu li a:hover {
 	background-color: #333;   
}

.notice-banner {
    color: #1e1e1e;
}

blockquote {
	border-left-color: #333;
}

body {
    background: #222224;
    color: #eee;
}
h1, h2, h3, h4, h5, h6 {
 	color: #eee;   
}

:not(pre) > code, .well, .cse .gsc-control-cse, .gsc-control-cse {
 	color: #ddd;  
    background: #2a2a2c;
    border: 1px solid #444;
}
pre {
 	border-color: #181818;   
}

hr, .prev-next-links.top, .prev-next-links.bottom  {
 	border-top-color: rgba(0,0,0,0);
    border-bottom-color: #333;
}

/* table */
.table th, .table td {
    border-color: #444;
}

table.table.table-bordered {
  	background: #222224;   
    border: 1px solid #444;
}
.table-bordered th, .table-bordered td {
	border-color: #444;
}

.table-hover>tbody>tr:hover {
 	background-color: #333; 
}

/* syntax highlighting */
html pre {
 	background: #181818;   
}
pre code {
	color: #d8d8d8;
    background: #181818;
}
pre .type {
 	color: #f7ca88;    
}
pre .kwd {
  	color: #ba8baf;   
}
pre .val {
 	color: #ab4642;   
}
pre .str {
 	color: #a1b56c;   
}
pre .str, div.pre .str, pre .str .kwd, pre .str .val, pre .str .type {
 	color: #a1b56c;
}
pre .cmt, pre .cmt .kwd, pre .cmt .str, pre .cmt .val, pre .cmt .type {
 	color: #585858;   
}
}

@-moz-document domain("api.haxe.org") {
/* api.haxe.org */
h3 code {
	box-shadow: none;
}

.nav-list li i.fa {
	color: #555;   
}

.nav-header {
 	color: #eee;  
}

code .identifier {
    color: #fff;
}

code .type {
    color: #08c; 
}

.nav-list>.active>a.treeLink, .nav-list>.active>a.treeLink:hover, .nav-list>.active>a.treeLink:focus{
 	background: #4a4a4c;
}

.nav>li>a:focus {
    color: #00aaff;
    background: #4a4a4c;
}

.nav-list>li>a.treeLink:hover, .nav>li>a:hover{
    color: #00aaff;
 	background: #2c2c2e;   
}

.dropdown-menu {
    background: #181818;
}

.dropdown-menu li a {
 	color: rgb(185,181,181);   
}

.dropdown-menu li a:focus {
    background-color: #333;
 	color: rgb(185,181,181);   
}

.section, .page-header {
 	border-bottom-color: #333;   
}

.field + .field {
 	border-top-color: #333;   
}

.label, .badge, h3 span.label {
    margin-right: 5px;
 	background-color: #666;   
    color: #fff;
}

.indent {
 	border-left-color: #333;
}

.availability {
    color: #ccc;
}
}
