css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url(http://macthemes2.net/), url-prefix(http://macthemes2.net) {

#macthemes-html body {
	background: url(http://istig.pinkmirrors.com/forums/gestapowinter/bodybg.jpg) repeat #000 !important;
}

#macthemes-html #wrapper {
	background: none !important;
}

#macthemes-html * {
	font-family: 'Georgia' !important;
	letter-spacing: -0.08em !important;
}

#macthemes-html p {
	line-height: 25px;
}

#macthemes-html * strong{
	letter-spacing: -0.05em !important;
}

#macthemes-html h2, #macthemes-html h3{
	letter-spacing: -0.05em !important;
}

#macthemes-html #logo {
	background: url(http://istig.pinkmirrors.com/forums/gestapowinter/logo.png) !important;
}

#macthemes-html #container-top {
	background: url(http://istig.pinkmirrors.com/forums/gestapowinter/container-head-u.jpg) no-repeat center top !important;
}

#macthemes-html #container-main {
	background: url(http://istig.pinkmirrors.com/forums/gestapowinter/container-tile.jpg) repeat-y center !important;
}

#macthemes-html #container-end {
	background: url(http://istig.pinkmirrors.com/forums/gestapowinter/container-footer.jpg) no-repeat bottom center !important;
}

#macthemes-html #frame-top {
	background: url(http://istig.pinkmirrors.com/forums/gestapowinter/forum-top.jpg) no-repeat !important;
}

#macthemes-html #frame-bottom {
	background: url(http://istig.pinkmirrors.com/forums/gestapowinter/forum-end.jpg) no-repeat bottom !important;
}

#macthemes-html #frame {
	background: url(http://istig.pinkmirrors.com/forums/gestapowinter/forum-tile.jpg) repeat-y !important;
}

#macthemes-html #brdtitle {
	background: url(http://istig.pinkmirrors.com/forums/gestapowinter/forum-badge.png) !important;
}

#macthemes-html #brdmenu, #macthemes-html #brdstats {
	border-top-color: #37270f !important;
}

#macthemes-html #nav-main {
	background: none !important;
}

#macthemes-html #nav-sub {
	display: none !important;
}

#macthemes-html legend {
	color: #5e6f88 !important;
	/*i-am: legend; */
}

#macthemes-html a {
	color: #5e6f88 !important;
}

#macthemes-html a:visited {
	color: #555c66 !important;
}

#macthemes-html #navindex a, #macthemes-html #navuserlist a, #macthemes-html #navrules a, #macthemes-html #navsearch a, #macthemes-html #navprofile a, #macthemes-html #navpm a, #macthemes-html #navlogout a {
	color: #333 !important;
}

#macthemes-html #n-news a {
	background: url(http://istig.pinkmirrors.com/forums/gestapowinter/nav-news.png) 0 0 !important;
}

#macthemes-html #n-news a:hover {
	background: url(http://istig.pinkmirrors.com/forums/gestapowinter/nav-news.png) 0 17px !important;
}

#macthemes-html #n-themes a {
	background: url(http://istig.pinkmirrors.com/forums/gestapowinter/nav-themes.png) 0 0 !important;
}

#macthemes-html #n-themes a:hover {
	background: url(http://istig.pinkmirrors.com/forums/gestapowinter/nav-themes.png) 0 17px !important;
}

#macthemes-html #n-insiders a {
	background: url(http://istig.pinkmirrors.com/forums/gestapowinter/nav-insider.png) 0 0 !important;
}

#macthemes-html #n-insiders a:hover {
	background: url(http://istig.pinkmirrors.com/forums/gestapowinter/nav-insider.png) 0 17px !important;
}

#macthemes-html #n-about a {
	background: url(http://istig.pinkmirrors.com/forums/gestapowinter/nav-about.png) 0 0 !important;
}

#macthemes-html #n-about a:hover {
	background: url(http://istig.pinkmirrors.com/forums/gestapowinter/nav-about.png) 0 17px !important;
}

#macthemes-html #n-forums a {
	background: url(http://istig.pinkmirrors.com/forums/gestapowinter/nav-forum.png) 0 0 !important;
}

#macthemes-html #n-forums a:hover {
	background: url(http://istig.pinkmirrors.com/forums/gestapowinter/nav-forum.png) 0 17px !important;
}

#macthemes-html #n-wiki a {
	background: url(http://istig.pinkmirrors.com/forums/gestapowinter/nav-wiki.png) 0 0 !important;
}

#macthemes-html #n-wiki a:hover {
	background: url(http://istig.pinkmirrors.com/forums/gestapowinter/nav-wiki.png) 0 17px !important;
}

#macthemes-html #footer p {
	color: /* #394659 */ transparent !important;
}

#macthemes-html #footer li {
	border-left: none !important;
}

#macthemes-html #footer a{
	color: transparent !important;
}

#macthemes-html #footer {
	background: url(http://istig.pinkmirrors.com/forums/gestapowinter/footerstuff.png) bottom center no-repeat !important;
}

#macthemes-html .isticky.rowodd {
	background-color: #cfd6e1 !important;
}

#macthemes-html .isticky.roweven {
	background-color: #e3e8f0 !important;
}

#macthemes-html .tcl, #macthemes-html .tc4, #macthemes-html .tc3, #macthemes-html .tc2, #macthemes-html .tcr{
	border-color: #fff !important;
}

}
