css:
@-moz-document domain("s-ul.eu") {
body {
    color: #fff;
    background-color: #333;
}

.panel {
    background-color: rgba(255, 255, 255, 0);
    border-radius: 0px;
}

div.panel.panel-primary {
    border-width: 2px;
    background-color: #333;
}

.panel-body {
    padding: 5px;
    background-color: #333;
}

.panel-heading {
    border-top-left-radius: 0px;
    border-top-right-radius: 0px;
}

a.list-group-item {
    color: #9d9d9d;
    background-color: #333;
    border-style: none;
}

a.list-group-item:hover {
    color: #fff;
    background-color: #333;
}

div.col-md-9 hr {
    border-style: none;
}

div.panel-footer.text-center {
    background-color: #333;
    border-style: none;
}

div.panel.panel-default {
    background-color: #333;
    border-style: solid;
    border-bottom-width: 1px;
    border-left-width: 1px;
    border-right-width: 1px;
    border-width: 1px;
    border-top-width: 1px;
}

div.footer-link {
    background-color: #333;
    border-style: solid;
    border-bottom-width: 0;
    border-left-width: 0;
    border-right-width: 0;
    border-width: 0;
    border-top-width: 2px;
    border-color: #3278B3;
}

.label-default {
    background-color: #395;
}

div.panel-body.text-center {
    color: #fff;
    background-color: #333;
}
div.well.well-lg.text-center {
    color: #fff;
    background-color: #222;
}
.open>.dropdown-menu {
    display: block;
    background-color: #333;
}
.dropdown-menu>li>a {
    color: #fff;
}
div.well {
    background-color: #222;
    border: 0px solid #e3e3e3;
}
tbody tr td {
    background-color: #222;
}
.page-header {
    padding-bottom: 9px;
    margin: 40px 0 20px;
    border-bottom: 0px solid;
}
.form-control {
    display: block;
    width: 100%;
    height: 34px;
    padding: 6px 12px;
    font-size: 14px;
    color: #fff;
    background-color: #333;
    background-image: none;
    border: 0px solid #000;
    border-radius: 4px;
}
div.col-md-9 pre {
    background-color: #1f1f1f;
    color: #ffffff;
}
.modal-header {
    border-bottom: 0px solid #fff;
}
.modal-footer {
    border-top: 0px solid #fff;
}
button.close span {
    color: #fff;
}
div.panel.panel-default {
    border-color: #1d1d1d;
}
div.modal-content {
    background-color: #1a1a1a;
}
.panel-default>.panel-heading {
    color: #fff;
    background-color: #1a1a1a;
    border-color: rgba(255, 255, 255, 0);
}
.panel {
    margin-bottom: 20px;
    background-color: #fff;
    border-radius: 4px;
    -webkit-box-shadow: 0 1px 1px rgba(0, 0, 0, .05);
    box-shadow: 0 1px 1px rgba(0, 0, 0, .05);
}
code {
    color: #333;
    background-color: #333;
    border-radius: 1px;
}
pre {
    border: 0px solid #ccc;
}
.table>tbody>tr>td,
.table>tbody>tr>th,
.table>tfoot>tr>td,
.table>tfoot>tr>th,
.table>thead>tr>td,
.table>thead>tr>th {
    padding: 8px;
    line-height: 1.42857143;
    vertical-align: top;
    border-top: 0px solid #ddd;
}
select {
    color: #fff;
    background-color: #333;
    border-width: 0px;
    border-color: #333;
}
.dmca-link {
    background-color: #222;
}
input.btn.btn-primary {
    border-width: 0px;
    color: #fff !important;
    background-color: #337ab7 !important;
}
button.btn.btn-primary {
    border-width: 0px;
    color: #fff !important;
    background-color: #337ab7 !important;
}
}
