css:
@-moz-document url-prefix("https://actie.alfa-college.nl/") {
#container {
    background: -webkit-linear-gradient(top, rgba(255, 255, 255, 1) 0%, rgba(255, 255, 255, 0) 0%);
    height: 100%;
}
#alfa-header {
    background: -webkit-linear-gradient(top, rgba(255, 255, 255, 1) 0%, rgba(255, 255, 255, 0) 0%);
}
.middle {
    background: -webkit-linear-gradient(top, rgba(255, 255, 255, 1) 0%, rgba(255, 255, 255, 0) 0%);
    width: 76em;
    float: right;
    clear: left;
    z-index: 10;
    position: absolute;
    left: 28%;
    top: 8em;
}
.left {
    width: 68em;
    height: 100em;
    float: right;
    clear: left;
    z-index: 10;
    position: absolute;
    left: 11.88%;
    top: 8em;
}
.block {
    width: 18.90em;
    clear: right;
}
body {
    background-attachment: fixed !important;
    background-image: url(http://www.whitetablegallery.org/src/share/default.jpg) !important;
}
*::-webkit-scrollbar {
    width: 10px ! important;
    height: 10px ! important;
    background: #1e1e1e ! important;
    border: 1px solid #252525 ! important;
}
*::-webkit-scrollbar-button {
    display: none ! important;
}
*::-webkit-scrollbar-thumb,
*::-webkit-scrollbar-track {
    border: 0 ! important;
    box-shadow: none ! important;
}
*::-webkit-scrollbar-thumb {
    min-height: 28px ! important;
    background: #C5CED1 ! important;
}
*::-webkit-scrollbar-corner,
*::-webkit-scrollbar-track {
    background: #1e1e1e ! important;
}
}

