css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("astralapp.com") {
  
  /***** app.astralapp.com/dashboard *****/
  
  /** Top, Left - 'Astral' **/
  .dashboard-sidebar .dashboard-sidebar-header {
    background-color: #202020;
  }
  
  /** Top Bar - 'All Stars' */
  /* Bkgd */
  .dashboard-dashboardHeader {
    background-color: #202020;
  }
  
  .dashboard-dashboardHeader h2 {
    color: #aaaaaa;
  }
  
  /* Tag Edit Pane */
  .dashboardHeader-editTagDropdown {
    background-color: #222222;
  }
  
  .dashboardHeader-editTagDropdown input[type="text"] {
    background-color: #333333;
  }
  
  /* Search */
  .dashboard-searchBar .dashboard-telescope, .dashboard-searchBar .dashboard-telescope:focus {
    background-color: #222222;
    color: #aaaaaa;
  }
  
  /** User Profile **/
  /* Pop-up Pane */
  .dashboard-userDropdownMenu {
    background-color: #202020;
  }
  
  .dashboard-userDropdownMenuItem:hover {
    background-color: #222222;
  }
  
  /* Settings Pop-up overlay */
  .settingsPanel-panel {
    background-color: #202020;
  }
  
  .settingsPanel-panelContent .settingsPanel-row {
    background-color: #202020;
    border-top-color: #333333;
    color: #aaaaaa;
  }
  
  .settingsPanel-header h3, settingsPanel-settingName {
    color: #aaaaaa;
  }
  
  /** Left Pane **/
  .dashboard-sidebar {
    background-color: #202020;
  }
  
  /* Sort Dropdown */
  .dashboard-sortTagsDropdownMenu {
    background-color: #222222;
  }
  
  /** Middle Project list **/
  .dashboard-repos {
    background-color: #161616;
  }
  
  /* unselected row */
  .repos .repo {
    background-color: #161616;
  }
  
  .repos .repo .repo-description {
    color: #aaaaaa;
  }
  
  /* selected row */
  .repos .repo.active {
    background-color: #3c3c3c;
  }
  
  /* hover row */
  .repos .repo:hover {
    background-color: #222222;
  }
  
  /** Right Project Details Pane **/
  
  /* 'Edit Tags', 'Notes' */
  .dashboard-repo-details .manage-star {
    background-color: #222222;
  }
  
  .dashboard-repo-details .manage-star label {
    color: #aaaaaa;
  }
  
  .dashboard-repo-details .manage-star button:not(.btn-flat), .dashboard-repo-details .manage-star .clone-url input[type="text"] {
    background-color: #2e2e2e;
  }
  
  /* Edit Tags - Popup Pane */
  .tag-editor {
    background-color: #222222;
  }
  
  .select2-container--default .select2-search--inline .select2-search__field,
  .select2-container--default .select2-selection--multiple, .select2-container--default.select2-container--focus .select2-selection--multiple { /* 'Add a Tag */
    background-color: #222222;
  }
  
  .select2-container--default .select2-results > .select2-results__options {
    background-color: #222222;
    color: #aaaaaa;
  }
  
  /* Notes - Popup Pane */
  .repo-notes {
    background-color: #222222;
  }
  
  .CodeMirror {
    background-color: #222222;
  }
  
  .editor-toolbar a {
    color: #aaaaaa !important;
  }
  
  .repo-notes .CodeMirror {
    color: #aaaaaa;
  }
  
  /* Notes - Popup Pane - Preview */
  .editor-preview {
    background-color: #2f2f2f;
  }
  
  .editor-toolbar.disabled-for-preview a:not(.no-disable) {
    background-color: #222222;
  }
  
  /* 'Clone' */
  .dashboard-repo-details .manage-star .clone-url input[type="text"], .dashboard-repo-details .manage-star .clone-url input[type="text"]:focus {
    background-color: #222222;
  }
  
  /* Main Content */
  .dashboard-repo-details {
    background-color: #161616;
    color: #a1a1a1;
  }
  
  /* Transition overlay */
  .dashboard-repo-details .readme-error-overlay, .dashboard-repo-details .readme-loading-overlay {
    background-color: rgba(22,22,22,.9); /* #161616; */
    color: #a1a1a1;
  }
  
  /***** astralapp.com *****/
  /* White Bkgd */
  .home-section.overview, .home-section.testimonials {
    background-color: #222222;
  }
  
  /* Green Bkgd */
  .home-section.features {
    background-color: #333333;
    background-image: none;
  }
  
  /* Footer */
  .footer {
    background-color: #222222;
    box-shadow: 0 -4px 0 0 #222222;
  }
  
  .home {
    background-color: #222222;
  }
  
}
