css:
@-moz-document regexp("https://.*\\.ryver.com/index\\.html.*") {
    *:not([data-presence-for]) {
        background: rgba(0, 0, 0, 0)!important;
        text-shadow: 0 0 10px;
        color: #c0c0c0;
    }

    .scene-space {
        background: linear-gradient(rgba(0, 0, 0, 0.1), rgba(0, 0, 0, 0.1)), url('https://raw.githubusercontent.com/Matthew-Smith/neon-slack/master/slackBG.jpg')!important;
        color: #c0c0c0;
        background-size: 100% 120%!important;
        background-repeat: no-repeat!important;
    }

    .tabbed__header {
        background-color: rgba(32, 32, 32, 0.9)!important;
        border-bottom: 1px solid #00C2FF;
    }

    .topic-bar {
        background-color: rgba(32, 32, 32, 0.9)!important;
    }

    .view-toolbar {
        background: rgba(32, 32, 32, 0.4)!important;
    }

    .view-body, .chat-common {
        background: rgba(0, 0, 0, 0.4)!important;
    }

    .chat-message__item.is-editing, .chat-message__item.is-selected {
        background: rgba(64, 64, 64, 0.7)!important
    }

    .is-link {
        color: #01FD86 !important
    }

    .navigation-help__link {
        color: #00C2FF;
    }

    .scene-space-tab-button--sticky:not(:first-child) > * {
        color: #00C2FF;
    }

    .markdown-editor.has-auto-height .markdown-editor__input {
        color: #eee;
    }

    .tabs-overflow-menu {
        border-radius: 0 0 5px 5px;
        border-top-width: 0;
    }

    .popup, .tabs-overflow-menu {
        background: rgba(32, 32, 32, 1)!important;
    }

    .smart__pane-1, .create-team-dialog__body, .create-forum-dialog__content {
        background-color: rgba(32, 32, 32, 1)!important;
    }

    div[class^='create-'] > label, div[class^='create-'] > div {
        color: #00C2FF!important;
    }

    .create-team-dialog__nickname > div span, .create-forum-dialog__nickname > div span {
        color: #01FD86 !important;
    }

    button {
        border-color: #00C2FF!important;
        color: #00C2FF!important;
    }

    [class^='icon'] {
        color: #00C2FF!important;
    }

    span, a {
        color: #c0c0c0;
    }

    [class^='navigation-help']:hover {
        color: #df142a!important;
    }

    .scene-space__navigation-instance, .scene-space__navigation-name {
        font-size: 1.5rem;
        font-weight: 900;
        color: #df142a;
    }

    .scene-space-tab-button--flash>.scene-space-tab-button__flash, .scene-space-tab-button--flash>.scene-space-tab-button__title {
        color: #01FD86;
    }

    .post-item__title, .post-item__action {
        color: #00C2FF!important;
    }

    [class^='user-list__menu'] {
        background: rgba(16, 16, 16, 0.9)!important;
        color: #00C2FF!important;
    }

    .scene-space__button-wrapper {
        border-right: 1px solid #00C2FF;
    }

    .hljs-attribute, .hljs-name, .hljs-tag {
        color: #00C2FF;
    }

    .hljs-keyword, .hljs-selector-tag, .hljs-subst {
        color: #bb77ff;
    }

    .scene-space-tab-button .icon-close {
        visibility: hidden;
    }
    .scene-space-tab-button:hover .icon-close {
        visibility: visible;
    }

    .scene-space.has-above-fold .scene-space__above-fold, .scene-space.has-below-fold .scene-space__below-fold {
        background: #00C2FF!important;
    }
    .scene-space.has-above-fold .scene-space__above-fold span, .scene-space.has-below-fold .scene-space__below-fold span {
        color: rgb(16, 16, 16)!important;
    }

    .preview-image-dialog {
        border: 3px solid #00C2FF;
        border-radius: 10px;
        background: rgba(64, 64, 64, 1)!important;
        -webkit-box-shadow: 0px 0px 50px 2px #00C2FF;
        -moz-box-shadow: 0px 0px 50px 2px #00C2FF;
        box-shadow: 0px 0px 50px 2px #00C2FF;
    }

    .preview-image-dialog__body {
        background: rgba(16, 16, 16, 1)!important;
        border-radius: 10px;
    }
}
