css:
@-moz-document url-prefix("https://webref.ru/") {
div.title {
    color: #8D6E63;
    transition: 1s 999999s;
}
div.title:hover {
    color: #4E342E;
    transition: 0.5s;
}
header {
    background-color: #ECEFF1;
}
#search-button {
	background-color: #CFD8DC;
    transition: 0.5s;
}
#search-button:hover {
	background-color: #FF5252;
    color: #fff;
    transition: 0.5s;
}
#cse-search-box {
    background-color: #FF5252;
    border-radius: 5px 0px 5px 5px;
}
#search-button.active {
    background-color: #FF5252;
    border-color: #FF5252;
}
#cse-search-input {
    border: 0px;
}
#ac-ref {
    border: 0px;
    background-color: #ECEFF1;
    transition: 0.5s;
}
#ac-ref:hover {
    background-color: #FAFAFA;
    transition: 0.5s;
}
#toc-html {
    border: 0px;
    background-color: #ECEFF1;
}
.region-sidebar {
    border: 0px;
    border-radius: 8px;
    background-color: #FAFAFA;
}
h2.block_title {
    color: #5D4037;
}
#views-css_cat-block {
    border: 0px;
    border-radius: 8px;
    background-color: #ECEFF1;
}
#menu-menu-css-fun {
    border: 0px;
    border-radius: 8px;
    background-color: #ECEFF1;
}
.spec {
    border: 0px;
    background-color: #ECEFF1;
}
.yes {
    background-color: #A5D6A7;
}
.no {
    background-color: #EF9A9A;
}
.warn {
    background-color: #FFF176;
}
.br td {
    background-color: #BCAAA4;
}
section.row.credit {
    border: 0px;
    background-color: #ECEFF1;
}
a:link {
    color: #3F51B5;
}
a:visited {
    color: #9C27B0;
}
pre {
    border: 0px;
    border-radius: 10px;
    background-color: #ECEFF1;
}
dl.param2 dt {
    color: #66BB6A;
}
table {
    border: 0px;
    border-radius: 5px;
    background-color: #ECEFF1;
}
.webrefExample-tabs {
    border: 0px;
    border-radius: 5px;
}
.value, pre .constant.numeric, pre .css-value, pre .hex-color, pre .keyword.unit {
    color: #66BB6A;
}
.tag {
    color: #3F51B5;
}
#views-tech-block, #menu-menu-help, #views-popular-featured, #menu-menu-css, #menu-menu-css-value {
    border: 0px;
    border-radius: 8px;
    background-color: #ECEFF1;
}
.category-wrap, .pager li {
    border: 0px;
    border-radius: 5px;
}
.pager a:hover {
    background: #ef5350;
    border-radius: 5px;
}
a, footer a, header a {
    transition: 0.5s;
}
a:hover, footer a:hover, header a:hover {
    color: #ef5350!important;
    transition: 0.5s;
}
.answering-form input[type=submit]:hover, .quiz-start-link:hover {
    background-color: #ef5350;
}
.answering-form input[type=submit], .quiz-start-link {
    background-color: #e57373;
    border: 0px;
}
aside .block a:hover {
    color: #ef5350!important;
}
img {
    border-radius: 5px;
}
.block .tag-item a.active:hover, .block .tag-item a:hover {
    background: #ef5350;
    border: 0px;
}
.tag-item a.active, .block .tag-item a {
    border: 0px;
}
.recommend-text {
    border-radius: 4px;
    transition: 0.5s;
}
.recommend-text:hover {
    background: #5C6BC0;
    border-radius: 4px;
    transition: 0.5s;
}
.page-previous a::after {
    border-color: transparent #f44336 transparent transparent;
}
.page-next a::after {
    border-color: transparent transparent transparent #f44336;
}
.page-next a:hover, .page-previous a:hover {
    background: #f44336;
}
.page-next a, .page-previous a {
    border-radius: 1px;
    transition: 0.5s;
}
.page-previous a:hover::after {
    border-color: transparent #f44336 transparent transparent;
}
.page-next a:hover::after {
    border-color: transparent transparent transparent #f44336;
}
}

@-moz-document url-prefix("https://webref.ru/") {
.copyright {
    display: none;
}
footer {
    display: none;
}
div.adv_footer {
    display: none;
}
}
