css:
@-moz-document domain(fastmail.com) {
    div.app-belowToolbar {
        background-color: #222;
    }
    div.app-main div.app-belowToolbar {
        border-left: 1px solid #000;
    }
    div.app-belowToolbar a.is-selected {
        background-color: #000;
        color: #eee;
    }
    div.app-belowToolbar a.v-PushSelect-option.is-selected {
        background-color: inherit;
    }
    div.app-belowToolbar a.v-PushSelect-option:hover {
        color: #eee;
        background-color: inherit;
    }
    div.app-belowToolbar a:hover {
        background-color: #333;
    }
    div.app-toolbar {
        border-bottom: 1px solid #000;
        background-color: #161616;
    }
    div.app-toolbar a:hover {
        color: #eee;
        background-color: #333;
    }
    div.app-toolbar div.v-Toolbar-section button.v-Button {
        border: 1px solid #444;
        background-color: #000;
        color: #999;
    }
    div.app-toolbar div.v-Toolbar-section button.v-Button:hover {
        background-color: #333;
        color: #eee;
    }
    div.app-toolbar div.v-Toolbar-section label.v-SearchText {
        background-color: #000;
        border: 1px solid #444;
    }
    label.v-SearchText input.v-Text-input {
        background-color: #000;
        color: #999;
    }
    div.v-StatusBar,
    div.v-UsageInfo {
        background-color: #000;
        color: #999;
        border: 1px solid #444;
    }
    div.v-Sidebar-editLink {
        border-top: 1px solid #000;
    }
    div.app-toolbar span.v-Toolbar-divider:first-child {
        border-left: 1px solid #444;
    }
    div.v-Mailbox div.v-MailboxItem {
        background-color: #161616;
        border-bottom: 1px solid #444;
    }
    div.v-Mailbox div.v-MailboxItem.is-unread span.v-MailboxItem-name,
    div.v-Mailbox div.v-MailboxItem.is-unread span.v-MailboxItem-subject {
        color: #eee;
    }
    div.app-viewBar {
        border-bottom: 1px solid #444;
    }
    h1.v-Thread-title {
        color: #eee;
    }
    div.v-Toolbar-section--right {
        color: #999;
    }
    div.v-Toolbar-section select.v-Select-input {
        background-color: #000;
        color: #999;
        border: 1px solid #444;
    }
    div#mail > div.app-main {
        background: #000;
    }
    .app-listItem::after {
        border: 0;
        background: #444;
    }
    div.app-list {
        background: -moz-linear-gradient(left, #404040 0%, #4f4f4f 45%);
    }
    div.app-listItem {
        background: -moz-linear-gradient(top, #292929 0%, #323232 45%);
    }
    div.app-source.is-selected {
        border-color: red;
    }
    .v-FolderSource.is-badged,
    .v-Sidebar--showRefresh .v-FolderSource.is-selected {
        border-color: #777;
        background: -moz-linear-gradient(top, #4f4f4f 55%, #404040 100%);
    }
    .app-main > .app-belowToolbar,
    .app-content {
        border-left: 1px solid #111;
    }
    .v-MainNavToolbar-userName {
        color: #fff;
        text-shadow: 0px 2px 1px #000;
    }
    .v-MainNavToolbar-appName {
        color: #fff;
        text-shadow: 0px 2px 1px #000;
    }
    div.app-toolbar {
        background: #EF4A34;
        border-top: 1px solid #555;
    }
    .app-source:hover {
        border-color: #333;
    }
    div.app-toolbar a:hover {
        background: -moz-linear-gradient(top, #ef4a34 53%, #c93b28 100%);
    }
    .v-MailboxItem-name > span {
        color: #EF4A34;
    }
    div.app-toolbar div.v-Toolbar-section button.v-Button {
        background: -moz-linear-gradient(top, #383838 0%, #000000 100%);
        border: 1px solid #000;
    }
    div.app-toolbar div.v-Toolbar-section button.v-Button {
        color: #f0f0f0!important;
    }
    .v-Mailbox--short .v-MailboxItem-subject {
        color: #fafafa;
    }
    .v-ThreadMessage a {
        color: red;
    }
    .v-Mailbox--short .v-MailboxItem-time {
        color: #aaa;
    }
    .v-MailboxItem-total {
        color: #f0f0f0;
    }
    .app-source {
        color: #ddd;
    }
    .v-FolderSource {
        color: #aaa;
    }
    .v-Mailbox--short > .v-MailboxItem {
        height: 85px;
    }
    .app-listItem-link:hover {
        background: #222!important;
        ;
    }
    .app-listItem.is-selected {
        background: #111!important;
    }
    label.v-SearchText input.v-Text-input {
        background: -moz-linear-gradient(top, #383838 0%, #000000 100%);
        color: #fff;
    }
    .v-ListKBFocus {
        background: #c00000;
    }
    div.app-belowToolbar a:hover {
        background: none;
    }
}
