css:
@-moz-document regexp('https?://1\\.next\\.westlaw\\.com.*') {
  
.co_document .co_starPage {
    color: #007236;
    font-weight: 700;
    font-style: normal;
    top: 0;
}

.co_starPage {
    background-color: #00ffff;
    border-style: solid;
    border-width: 0;
    font-family: monospace;
    font-size: 12pt;
    font-weight: bold;
    padding-bottom: 2px;
    padding-left: 2px;
    padding-right: 4px;
    padding-top: 1px;
    
     border:2px solid #007236;
     -moz-border-radius:10px;
     -webkit-border-radius:10px;
     border-radius:10px;
     width:250px;
}


.co_footnoteNumber {
    background-color: #00ff00;
    
     border:2px solid #00ff00;
     -moz-border-radius:10px;
     -webkit-border-radius:10px;
     border-radius:10px;
     width:250px;
}

.co_footnoteReference {
    background-color: #00ff00;
    border-color: #d7e0d8;
    border-style: dotted;
    border-width: 0;
    color: #000000;
    font-family: monospace;
    font-weight: bold;
    padding-bottom: 2px;
    padding-left: 2px;
    padding-right: 2px;
    padding-top: 0px;
    
     border:2px solid #00ff00;
     -moz-border-radius:10px;
     -webkit-border-radius:10px;
     border-radius:10px;
     width:250px;
}

.co_partyLine{
    border-color: #12ba8a;
    border-style: solid;
    border-left-width: 5px;
    border-right-width: 0;
    border-top-width: 0;
    border-bottom-width: 0;
    background-color: #edffff;
}


.co_document_icon_previouslyviewed   {
    background-color: #D9E9FF;
    border-color: #4592FF;
    border-style: solid;
    border-width: 3px;
}

.co_headtext {
    background-color: #e6e6e6;
    border-color: #807c80;
    border-style: dashed;
    font-weight: bold;
    padding-bottom: 0;
    padding-left: 8px;
    padding-right: 8px;
}

.co_hovertext {
    background-color: #c4e0ff;
}

.co_panelBlock {
    background-color: #99ff99;
    font-weight: bold;
}

.co_paragraphText .co_link {
    background-color: #eff8fc;
    border-color: #b6b5eb;
    border-style: dotted;
    border-width: 1px;
}

.x_concurranceAuthorLine {
    background-color: #e6e600;
    font-weight: bold;
}

.x_dissentAuthorLine {
    background-color: #ff4d4d ;
    font-weight: bold;
}

.x_leadAuthorLine {
    background-color: #99ff99;
    font-weight: bold;
}

.x_opinionCipdip {
    background-color: #ffffcc;
}

.x_opinionConcurrance {
    background-color: #FFFFF2;
}

.x_opinionDissent {
    background-color: #ffe6e6;
}

.x_voteLine {
    background-color: #e6e6e6;
    font-weight: bold;
}

a.co_link em {
    background-color: #c5e8fa;
    border-style: none;
    border-width: .5px;
}

div#co_rightColumn {
    display: none;
}

div.co_contentBlock.co_briefItState.co_panelBlock div {
    background-color: #99ff99;
    font-weight: bold;
}

div.co_contentBlock.x_leadAuthorLine {
    background-color: #99ff99;
    font-weight: bold;
}

div.co_headtext {
    border-color: #9e9e9e;
    border-left-width: 1px;
    border-right-width: 1px;
    border-top-width: 1px;
}

div.co_messageBoxContent {
    border-color: #ff0000;
    border-style: solid;
    border-width: 5px;
}

div.co_paragraphText {
    border-style: solid;
}

div.co_paragraphText.co_indentLeft1 {
    border-color: #e6e6e6;
    border-left-width: 15px;
    border-style: solid;
}

div.co_paragraphText.co_indentLeft2 {
    border-color: #c2c2c2;
    border-left-width: 30px;
    border-style: solid;
}

div.co_paragraphText.co_indentLeft3 {
    border-color: #8a8a8a;
    border-left-width: 45px;
    border-style: solid;
}

img.co_relatedInfo_HistoryItem_Quote {
    border-color: #23e312;
    border-style: solid;
    border-width: 3px;
    margin-left: 4px;
    margin-top: 0;
    padding: 1px;
}

li#co_powByKCContainer {
    display: none;
}

}
