css:
@-moz-document url-prefix("https://rachelbythebay.com/w/") {
	body {
    	font-family: -apple-system, BlinkMacSystemFont, "Segoe UI", "Roboto", "Oxygen", "Ubuntu", "Cantarell", "Fira Sans", "Droid Sans", "Helvetica Neue";
    	max-width: 640px;
		width: auto;
      	padding-left: 10px;
      	padding-right: 10px;
    	background: #fff;
		font-size: 16px;
	}

	.header {
    	background: none;
	    border-bottom: solid 1px #aaa;
	}	

	.date {
    	text-align: left;
    	margin-top: 24px;
    	background: none;
	}

	.post {
    	padding: 10px 0;
    	font-size: 1.250em;
    	line-height: 1.4;
    	font-weight: 300;
	}
  	
  	.footer {
      	background: none;
		padding: 40px 10px;
    }
}
