css:
@-moz-document domain("gravitytales.com") {
body.custom-background { 
    background: url("http://i.imgur.com/StkKWzw.jpg") fixed no-repeat center right;
    background-size: cover;
  }
  
#header-text-nav-container {
    background-color: rgba(0, 0, 0, 0);
}  

.container.wrap {
    background: rgb(0, 0, 0);
}

#main {
    background-color: rgba(0, 0, 0, 0.22);
}

div.entry-content.clearfix {
    display: block;
    background-color: rgba(0, 0, 0, 0.57);
    color: #ADA5A5;
    padding-left: 10px;
    padding-right: 10px;
    border-radius: 10px;
    font-weight: bold !important;
    font-family: "Comic Sans MS", cursive, sans-serif !important;
}

.entry-content p {
    font-family: "Comic Sans MS", cursive, sans-serif !important;
}
    

#secondary .widget, .error-404 .widget {
    margin-bottom: 35px;
    font-size: 14px;
    background-color: rgba(0, 0, 0, 0.84);
    color: #726c6c;
    padding-left: 10px;
    padding-right: 10px;
    border-radius: 10px;
    font-weight: bold !important;
    margin-top: 5px;
}

a:visited {
    color: rgb(167, 30, 30) !important;
}

a {
    color: #289dcc !important;
    font-weight: bold !important;
}

.news-bar {
    background-color: rgba(0, 0, 0, 0.56);
}

#site-navigation {
    box-shadow: 0 0 2px rgba(0, 0, 0, 0.1) inset;
    position: relative;
    background-color: rgba(0, 0, 0, 0.56);
    border-top: 4px solid #289dcc;
}

.type-post .entry-title, .type-page .entry-title {
    font-size: 34px;
    line-height: 1.2;
    font-weight: bold;
}

input.s {
    width: 60%;
    padding: 4px 54px 4px 10px;
    height: 36px;
    color: #777777;
    background-color: #1b1a1a;
    margin: 0;
    border: 1px solid #EAEAEA;
}

.wpdiscuz-subscribe-bar, #wpcomm .wc-form-wrapper, .wpdiscuz-front-actions, #wpcomm .wc-comment .wc-comment-right, .footer-socket-wrapper, #wpcomm .wc-comment-title, #wpadminbar {
    background: rgba(0, 0, 0, 0.56) !important;
    border-radius: 10px;
}

#wpcomm .wc-field-comment .wc-field-textarea textarea, input[type="text"], input[type="email"], input[type="password"], textarea {
    border: none!important;
    resize: none;
    padding: 10px;
    vertical-align: top;
    background-color: black;
}

#wpcomm .wc-comment-text p {
    color: #bd1313 !important;
    font-weight: bold !important;
    border-radius: 10px;
}

h1  {
    color: #02ab02 !important;
}

td {
    text-align: left;
    background-color: #111;
    color: #68c9e8;
}

tr td p {
    color: #ADA5A5 !important;
}

.main-navigation a:hover, .main-navigation ul li.current-menu-item > a, .main-navigation ul li.current_page_ancestor > a, .main-navigation ul li.current-menu-ancestor > a, .main-navigation ul li.current_page_item > a, .main-navigation ul li:hover > a, .main-navigation ul li ul li a:hover, .main-navigation ul li ul li:hover > a, .main-navigation ul li.current-menu-item ul li a:hover {
    background-color: #8a1212;
    color: #30cc0c !important;
}

.above-entry-meta span a {
    color: white !important;
}

.side-nav {
    background: #333;
}

.side-nav .widget {
    background: #2d2929;
}

.comments-widget .comment-img {
    background: #000000;
    padding: 8px 0;
    text-align: center;
    overflow: hidden;
}

.comments-widget .comment-details {
    padding: 10px 15px;
    background-color: black;
}

.ui-68 .form-control {
    border: 0;
    color: #999;
    box-shadow: none;
    padding: 18px;
    border-radius: 0;
    background-color: black;
}

.commentContent p:last-child {
    margin-bottom: 0;
    color: #2b8ecc;
}

.comments-widget .comment-bar .comment-poster {
    font-weight: 700;
    color: #fffffd;
}

div#chapterContent {
    -webkit-user-select: all !important;
    color: #999
}

h3, .h3 {
    color: #289dcc;
}
}
