css:
@-moz-document domain("tumblr.com") {


.svg-logo,
.logo .png-logo,
.logo:hover .opacity-switch .preload-container {
	background: url('https://i.imgur.com/HRcYeOr.png*[[logo1]]*//*[[logo2]]*/ /*[[logo3]]*/')!important;
	background-size: contain !important;
	background-repeat: no-repeat !important;
	width: 178px !important;
	height: 64px;
	/*	position: inherit; top: 5px;*/
	
	padding: 0px 0;
	margin-left: 5px;
	margin-top: -3px;
}
.logo:hover .opacity-switch .png-logo {
	opacity: 1;
}
.opacity-switch .preload-container {
	display: none;
}
.tab_bar {
	right: 5px!important;
}
.popover_menu_item .thumb {
	right: 33px !important;
}
.search_popover * {
	box-sizing: border-box;
	width: 178px;
}
.logo .large-shadow,
.logo .small-shadow,
.logo .outer,
.logo .inner {
	display: none!important;
}
}
