css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("about.com"), domain("verywell.com"), domain("thebalance.com"){

html, body, .navbar, .navbar-menu > li.navbar-submenu.inactive, .p-bg, .slab, .header, .chaptered-list, .widget-heading-title, .carousel-slide, .channel-row li, .bx-controls a, .content, .no-image, .block, .taxonomy-list, .l-header, .leaderboard.dynamic, .figure-article-caption
{color: #919191 !important;
background: #111 !important;
box-shadow: none !important;}
  
h1, h2, h3, h4, h5, h6, .search-result-header-title, .search-result-abstract, .partial-related-search-title, .partial-external-search-section-title, .block-excerpt
{color: #919191 !important;}    
  
a, .search-result-url, .partial-external-search-result-display-url, h2.block-title, .byline a, .article-content a, .adventure-nav-item a:hover .block-title span
{color: #00adee !important;
text-shadow: none !important;}

a:visited 
{color: #00668c !important;} 
  
.share-btns, #footer_1-0, #leaderboard_1-0, .spotlight-billboard, #text-ad-block-0, .l-footer, .share-item
{display: none !important;}
  
.input-group, .overlay
{color: #919191 !important;
background: #111 !important;
border: 1px solid #00adee !important;
box-shadow: none !important;} 
  
.partial-search-box-container, .pagination a
{color: #919191 !important;
background: #222 !important;
border: 1px solid #00adee !important;
box-shadow: none !important;}   
  
.input-group-btn, .btn, input 
{color: #919191 !important;
background: #222 !important;
border: none !important;}
  
.partial-search-box-button 
{height: 0px !important;
top: 10px !important;
width: 40px !important;
background: #222 !important;}  
  
.overlay li a:hover, .partial-pagination-active, .pagination a.selected  
{color: #111 !important;
background: #00adee !important;} 
  
.spotlight-blocks
{margin-right: 0rem !important;}
 
.block-media img 
{mix-blend-mode: normal !important;}
  
.section-title 
{border-top: solid 1px #00adee !important;}

.header 
{border-bottom: solid 1px #00adee !important;}
  
.btn
{overflow: hidden !important;} 
   
}
