css:
@-moz-document domain(www.ogre3d.org) {

/* Replace 4863 with your own ID. (It is the last numerical digits when visit your membership page).
*/
li.row dt a[href$="u=4863"], li.row dt:hover a[href$="u=4863"], li.row dt a[href$="u=4863"]:hover
{
 background-color: red !important;
 font-weight: normal;
 color: white !important;
 padding: 1px !important;
}

div.online
{
 background-image: none !important;
}

div.online dl.postprofile
{
 background: url('http://www.ogre3d.org/forums/styles/proogre/imageset/en/icon_user_online.gif') top right no-repeat !important;
}

div.postbody
{
 float: right !important;
}

dl.postprofile
{
 width: 225px !important;
 border-right: 1px solid white !important;
 border-left: 0 !important;
}

dl.postprofile dd
{
 font-size: smaller !important;
}

dl.postprofile a
{
 font-size: 1.25em !important;
}

dl.postprofile img
{
 margin: 0.5em 0 1em 0 !important;
}

div.forumbg ul li
{
  border: 0 !important;
}

div.forumbg ul li:hover a
{
 color: white !important;
}

div.forumbg ul li dt a.topictitle
{
 font-size: 1.25em !important;
}

div.postbody div.content
{
 padding-left: 4px !important;
}

div.postbody p.author img
{
 display: none !important;
}

div.postbody h3, div.postbody p.author
{
 font-size: 10px !important;
 display: inline !important;
 margin-bottom: 5px !important;
 padding-left: 3px !important;
 line-height: 1 !important;
}

div.unreadpost div.postbody h3
{
 background-color: #FFCCCC !important;
 border-left: 4px solid orangered !important;
}

li.sticky dl.icon
{
 opacity: 0.75;
 padding-left: 0 !important;
}

li.sticky dl.icon dt
{
 font-size: smaller !important;
}

li.row dl dt
{
 background-color: inherit !important;
}

li.row dl.icon
{
 background: none !important;
}

li.row dl dt[title="New posts"]
{
 background-color: #FFCCCC !important;
}

li.row:hover
{
 background-color: red !important;
 color: white !important;
}

li.row:hover dl dt
{
 background-color: transparent !important;
}

}
