css:
@-moz-document url-prefix("https://www.google.com/_/chrome") {
body { display: none !important; }
}

@-moz-document domain("douban.com") {
.bs-header, .post-ft, .footer, .meta { display: none !important; }
.container { margin: auto !important; padding: 1em 0 !important; width: 80% !important; max-width: none !important; }
img { margin: auto !important; }
.img_desc, h1 { text-align: center; }
}

@-moz-document domain("facebook.com") {
#rightCol, #leftCol, #pagelet_composer, #recent_capsule_container + div, #recent_capsule_container > div:nth-child(1), #pagelet_dock, .rfloat, .prs, .fss { display: none !important; }
#contentArea { width: 100% !important; }
#contentArea, #contentCol { padding: 0 !important; margin: 0 !important; }
.timelineLayout #contentArea, #content > div:nth-child(1) > div:nth-child(1), #content > div:nth-child(1) > div:nth-child(1) > div { width: 100% !important; }
#globalContainer, #blueBarDOMInspector > div > div > div > div { width: 80% !important; }
#recent_capsule_container { float: none !important; margin: 0 !important; width: 100% !important; }
.fbTimelineSection > div:nth-child(1) { width: auto !important; }
div, button, img, ::after { border: none !important; border-radius: 0 !important; box-shadow: none !important; }
}

@-moz-document domain("jianshu.com") {
h1 { text-align: center !important; }
.navbar, .wrap-btn, .fixed-btn, .include-collection, .author-info, .support-author, .meta-bottom, .visitor_edit, .comment-list, .meta-top { display: none !important; }
body, .article { padding: 0 !important; margin: 0 !important; }
.article { width: 100% !important; }
.container { width: 80% !important; }
}

@-moz-document domain("qq.com") {
.qr_code_pc, #feedly-mini, .rich_media_thumb, .rich_media_meta_list, p br { display: none !important; }
.rich_media { width: 80% !important; }
.rich_media_inner { padding: 0 !important; }
.rich_media_area_primary { padding: 20px 0 0 0 !important; }
}

@-moz-document domain("nytimes.com") {
div, h2 { font-family: sans-serif !important; }
h2 { font-style: normal !important; }
}

@-moz-document domain("wikipedia.org") {
#mw-panel, #mw-head, .noprint, #siteNotice, .mw-editsection, #footer, #catlinks, .navbox { display: none !important; }
#content { margin: 0 !important; padding: 1.5em 3em !important; border: none !important; }
#toc { width: 100% !important; box-sizing: border-box !important; }
.reflist { column-width: auto !important; }
}

@-moz-document domain("zhihu.com") {
.global-header, .meta, .view-more, .qr, .footer, .img-mask, .img-wrap img, .img-source, .question-title, #header-holder, .entry-title-image, .entry-meta, footer, .recommend-posts, .zu-top, .zu-main-sidebar, .zm-tag-editor, .zm-item-meta, .zh-answers-title, .zm-votebar, .answer-head, .more-awesome, .awesome-answer-list, .zh-footer { display: none !important; }
body, .zg-wrap { padding: 0 !important; background: none !important; }
.zu-main-content-inner { margin: 0 }
.main-wrap, .main, .zg-wrap { width: 80% !important; max-width: none !important; }
.content img { margin: auto !important; }
.headline, .question, .zm-item-answer { border: none !important; }
.headline-title, .entry-title, #zh-question-title { position: relative !important; text-align: center !important; text-shadow: none !important; color: #444 !important; }
}

@-moz-document domain("zreading.cn") {
body { background: none !important; }
#header, #navigation, #sidebar, #crumb, .wumii-hook, #bdshare, #comments, #footer, .link-back2top, .entry-meta, .single-nav { display: none !important; }
#content { width: auto !important; border: none !important; }
h2 { text-align: center !important; font-size: 1.5em !important; padding: 1em 0 !important; }
#wrap { width: 80% !important; }
img { border: none !important; padding: 0 !important; }
}
