@-moz-document domain("gamezer.com") {
body {
    background-color: #0d0d0d;
    color: #ff0000;
}
#HeaderLeft,
#HeaderRight {
    color: #37487f;
    font-size: 14px;
    font-weight: 800;
}
#RoomMenuContainer {
    height: 45px;
    position: relative;
    overflow: hidden;
    border-radius: 6px;
    background-color: #000000;
}
#TabContainer {
    height: 45px;
    background-color: #000000;
    overflow: hidden;
    border-radius: 6px 6px 0 0;
}
#PanelRight {
    right: 0;
    overflow: visible;
    border-radius: 6px 6px 0 0;
    box-shadow: 0px 4px 0px 0px #000000, 0px 1px 3px rgba(0, 0, 0, 0.3);
    -moz-box-shadow: 0px 4px 0px 0px #e0e6f7, 0px 1px 3px rgba(0, 0, 0, 0.3);
    -webkit-box-shadow: 0px 4px 0px 0px #010a23, 0px 1px 3px rgba(0, 0, 0, 0.3);
}
.Switch_Room #RoomMenuContainer {
    background-color: #000000;
}
leftBlock,
.centerBlock,
.rightBlock {
    -webkit-transform: translate3d(0, 0, 0);
    background-color: #010e33;
}
RoomMenu_Room {
    display: none;
    color: #0132d8;
    padding-left: 66px;
    overflow: hidden;
}
#Block_Chat {
    padding: 8px 0 100px 0;
    overflow: hidden;
    background-color: #000000;
}
#RoomChatPanel {
    width: 100%;
    height: 100px;
    position: absolute;
    bottom: 0;
    background-color: #000;
}
#RoomChatControls {
    width: 100%;
    height: 36px;
    padding-left: 10px;
    background-color: #000000;
}
#RoomChatButton {
