    font-size: 15px;
    font-weight: bold;
}
.RoomItem_users {
    width: 30px;
    height: 32px;
    position: absolute;
    right: 3px;
    margin-top: 6px;
    padding-top: 10px;
    border-radius: 9px;
    border-top-left-radius: 9px;
    border-top-right-radius: 9px;
    border-bottom-right-radius: 9px;
    border-bottom-left-radius: 9px;
    background-color: #5f76c4;
    text-align: center;
    font-weight: 800;
    color: #000;
    font-size: 16px;
}
leftBlock,
.centerBlock,
.rightBlock {
