css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("wikipedia.org") {
/*Just a little better Wikipedia*/
/*Author: Grawl (http://grawl.ru)*/
  /* @import 'https://fonts.googleapis.com/css?family=PT+Serif'; */
  #mw-content-text > p, 
  #mw-content-text > ul li,
  #mw-content-text > h1,
  #mw-content-text > h2,
  #mw-content-text > h3,
  #mw-content-text > h4,
  #mw-content-text > h5,
  #mw-content-text > h6
  {
    font-size: 1.2rem;
    font-family: "PT Serif", serif;
    font-weight: normal;
  }
  #mw-content-text > p, 
  #mw-content-text > ul li {
    max-width: 50rem;
  }
  #mw-content-text > h1 {
    /* not sure */
    /* font-size: 2.5em; */
  }
  #mw-content-text > h2 {
    font-size: 2em;
  }
  #mw-content-text > h3 {
    font-size: 1.75em;
  }
}
