css:
@-moz-document domain("volaretranslations.com") {
body, body div.content.main-content {
    background-color: transparent !important;
}

.panel-default>.panel-heading {
    background-color: #000000 !important;
}

* {
    font-weight: bold !important;
}

html {
    background:url(http://i.imgur.com/fFB163G.jpg) no-repeat center fixed;
    background-size:cover;-o-background-size:cover;
    -webkit-background-size:cover !important;
}

.mythemes-view-classic.boxed article .panel-default>.panel-heading, .panel-heading, div.panel-body {
    Background-color: black !important;
}

div.post-content, div.post-content p {
    color: #dc1a1a;
}

.mythemes-view-classic.boxed article, aside.mythemes-content-sidebar div.widget, div.comments-list div.mythemes-comments, p.message.register, form#registerform, form#loginform, .login #login_error, div#login_error {
    Background-color: rgba(0, 0, 0, 0.89) !important;
    border-radius: 20px;
}

div.post-content-wrapper {
    Background-color: rgba(0, 0, 0, 0.00) !important;
}

.login #backtoblog, .login #nav {
    Background-color: rgba(0, 0, 0, 0.91) !important;
    border-radius: 20px;
}
    

div.mythemes-comments, #comment, #author, #email, #url, .login .message, .login form, input#user_login.input {
    Background-color: rgba(27, 26, 26, 0.89) !important;
    border-radius: 20px;
}

input#user_login.input, input#user_email.input, .login form .input, .login form input[type=checkbox], .login input[type=text] {
    Background-color: rgb(250, 255, 189) !important;
    border-radius: 20px;
}

h4.widget-title, aside.col-sm-4.col-md-4.col-lg-4.mythemes-content-sidebar.sidebar-to-right {
    border-radius: 20px;
}

input#s2email, aside.mythemes-content-sidebar div.widget h4.widget-title, div.comments-list div.mythemes-comments, div.mythemes-author-bio, .login form input[type=checkbox], .login input[type=text], .status-publish.hentry {
    Background-color: rgba(0, 0, 0, 0.89) !important;
    border-radius: 20px;       
    margin-top: 20px;
}

#wpbody-content {
    Background-color: rgba(0, 0, 0, 0.81) !important
}

pre {
    Background-color: rgba(0, 0, 0, 0.81) !important;
    color: white !important;
}

input#s2email {
    color: #d01c1c;
}

time.last_post_time {
    color: darkred !important;
}

aside.mythemes-content-sidebar div.widget label {
    padding: 20px 20px 0px 20px;
    margin: 0px;
    color: #3eca1d;
}

h4.widget-title, p#nav > a:nth-child(n), p#backtoblog > a, p.message.register, .login #login_error {
    color: #bf1111 !important;
}

aside.mythemes-content-sidebar div.widget div > ul, aside.mythemes-content-sidebar div.widget > ul, .status-publish.hentry {
    padding-left: 15px !important;
    padding-right: 15px !important;
}

a, div.widget a, div.widget.mythemes_widget_post_tags div.tagcloud a:hover, div.widget.widget_tag_cloud div.tagcloud a:hover, aside.mythemes-footer-sidebars div.widget a, div.widget.mythemes_widget_articles ul li a, div.mythemes-header-front-page-sidebars div.widget a, aside.mythemes-content-sidebar div.widget a, aside.mythemes-content-sidebar div.widget.widget_calendar table td a:hover, aside.mythemes-content-sidebar div.widget.mythemes_widget_post_categories ul li a:hover, div.mythemes-header-front-page-sidebars div.widget.widget_calendar table td a:hover, div.mythemes-header-front-page-sidebars div.widget.widget_archive ul li a:hover, div.mythemes-header-front-page-sidebars div.widget.widget_categories ul li a:hover, div.mythemes-header-front-page-sidebars div.widget.widget_nav_menu ul li a:hover, div.mythemes-header-front-page-sidebars div.widget.widget_pages ul li a:hover, div.mythemes-header-front-page-sidebars div.widget.widget_post_categories ul li a:hover, aside.mythemes-content-sidebar div.widget.widget_archive ul li a:hover, aside.mythemes-content-sidebar div.widget.widget_categories ul li a:hover, aside.mythemes-content-sidebar div.widget.widget_nav_menu ul li a:hover, aside.mythemes-content-sidebar div.widget.widget_pages ul li a:hover, aside.mythemes-content-sidebar div.widget.widget_post_categories ul li a:hover, div.mythemes-header-front-page-sidebars div.widget.widget_rss ul li a.rsswidget:hover, aside.mythemes-content-sidebar div.widget.widget_rss ul li a.rsswidget:hover {
    color: #11b511;
}

a, a:hover, a:focus {
    text-decoration: none !important;
    -webkit-transition: all 0.3s linear !important;
    -moz-transition: all 0.3s linear !important;
    -ms-transition: all 0.3s linear !important;
    -o-transition: all 0.3s linear !important;
    transition: all 0.3s linear !important;
}

.panel-title {
    color: #226da5;
}

div.widget.widget_text div.textwidget, div.widget.widget_text div.textwidget p {
    font-size: 12px;
    line-height: 18px;
    color: #1982de;
}

.mythemes-view-classic article h2.post-title a, h1.post-title, h3 {
    color: #166cb9 !important;
}

.sr-only {
    position: inherit !important;
    line-height: 1.5;
}

div.post-actions a.action-item {
    background-color: #5f5b5b;
    color: white;
}

.post-avatar--troll, label {
    color: white !important;
}    

.mythemes-author-bio .mythemes-author-description, p {
    color: #bbb9b9 !important;
}

div.comment-respond h3.comment-reply-title {
    color: #e62323;
}

div.comment-respond form.comment-form p.logged-in-as, div.comment-respond form.comment-form p.comment-notes {
    font-size: 12px;
    color: #ffdd2d;
}

select, textarea, input[type="tel"], input[type="url"], input[type="text"], input[type="email"], input[type="phone"], input[type="number"], input[type="password"] {
    background-color: rgba(51, 51, 51, 0.78);
    border: none;
    color: goldenrod !important;
    border-radius: 20px;
}

div.comments-list div.mythemes-comments > ol li.pingback div.comment-quote, div.comments-list div.mythemes-comments > ol li.comment div.comment-quote, .c2 {
    color: #d20707;
}

h1, .h1, h2, .h2, h3, .h3, .form-table th, .form-wrap label {
    margin-top: 20px !important;
    color: white;
}

.login #pass-strength-result, address, abbr {
    color: #ff0606;
}

.color-option.selected, .color-option:hover {
    background: #8c1b1b;
}

.form-wrap p, p.description, p.help, span.description {
    font-size: 13px;
    font-style: italic;
    color: white !important;
}

div#wpbody-content {
    padding-left: 15px;
}

p:last-child {
    padding-bottom: 25px;
    padding-left: 15px;
    margin-bottom: 0px;
}

.c15 {
    background-color: #000000;
}

.post-avatar--troll {
    text-align: center;
    line-height: 28px;
    font-size: 16px;
    font-weight: bold;
    color: rgb(243, 0, 0) !important;
}

div.comments-list div.mythemes-comments > ol li.comment header cite a {
    color: #888 !important;
}
}

@-moz-document domain("volaretranslations.com") {
body::-webkit-scrollbar {
    width: 0.5em;
}
 
body::-webkit-scrollbar-track {
    -webkit-box-shadow: inset 0 0 6px dimgray;
    
}

::-webkit-scrollbar-button {
      background-color: dimgray;
 color: black;   
}

::-moz-selection {
    background-color: #000000 !important;
    color: #c71e1e !important;
}

/* Works in Safari */

::selection {
    background-color: transparent !important;
    color: Goldenrod !important;
}

input::-webkit-input-placeholder,
textarea::-webkit-input-placeholder {
    color: white !important;
    text-shadow: none!important;;
    -webkit-text-fill-color: initial!important;;
}

/*  */.morph:hover {
    border-radius: 50%;
    -webkit-transform: rotate(360deg);
    -moz-transform: rotate(360deg);
    -o-transform: rotate(360deg);
    -ms-transform: rotate(360deg);
    transform: rotate(360deg);
}

::-webkit-scrollbar-track, div.CodeMirror-vscrollbar {
    background-color: black !important;
        border-radius: 5px !important;
}

::-webkit-scrollbar-thumb {
    border-radius: 10px !important;
    background-color: #D62929;
    background-image: -webkit-linear-gradient(90deg, rgba(0, 0, 0, 0.52), rgba(0, 0, 0, 0), rgba(139, 0, 0, 0), rgba(0, 0, 0, 0), rgba(0, 0, 0, 0.52));
}

}
