css:
@-moz-document domain("wuxiaworld.com") {
* {
    font-weight: bold !important;
}

.post, #comments, .hentry, .error-404, .not-found, .searchPage, footer#colophon.site-footer {
    background: rgb(0, 0, 0) !important;
    width: 884px;
    padding: 0 30px 0 30px;
    font-size: 14px;
}

p {
    color: #999 !important;
}

a {
    color: #0f74de !important;
    text-decoration: none;
    border-bottom: 1px solid #000;
}

.post, #comments, .hentry, .error-404, .not-found, .searchPage {
        background: rgb(0, 0, 0) !important;
    width: 884px;
    padding: 0 30px 0 30px;
    font-size: 14px;
    border-radius: 20px;
}

.entry-footer, span.title, #bar-status div {
    color: #d21010 !important;
}

.entry-meta, span.ww_elapsed_time {
    color: #bdbdbd !important;
}

span.right {
    color: #999 !important;
}
                                            
div.collapsible-header, #wpcontent, #wpfooter {
    Background-color: black !important;
}

input[type=text], input[type=search], input[type=radio], input[type=tel], input[type=time], input[type=url], input[type=week], input[type=password], input[type=checkbox], input[type=color], input[type=date], input[type=datetime], input[type=datetime-local], input[type=email], input[type=month], input[type=number], select, textarea {
    Background-color: black !important;
    color: #0070ff;
}

#text-10 .textwidget {
    font-size: 14px!important;
    padding: 10px 10px 10px 10px!important;
    color: #c1bfbf;
    background-color: black !important;
}

.widget {
    background: rgb(0, 0, 0) !important;
    color: #1485a9 !important;
}

.widget_search .search-field, div.site-info {
    background: rgb(33, 32, 32) !important;
    color: #bf1515 !important;
}

.widget input[type=submit] {
    background: rgba(74, 74, 74, 0.9) !important;
}

.widget .widget-title, .widget {
    border-radius: 20px;
}

footer#colophon.site-footer {
    background: rgb(0, 0, 0) !important;
    width: 884px;
    padding-top: 10px;
    font-size: 14px;
}

h1, h2, h3, h4, h5, h6, .site-content article {
    font-size: 18px;
    color: #c51919 !important;
}

.login #login_error, .login .message, .login form {
    background: #000000 !important;
}

.login form .input, .login form input[type=checkbox], .login input[type=text] {
    background: #353434;
    color: #bf1111;
}
    
#loginform #jetpack-sso-wrap, #loginform #jetpack-sso-wrap *, td {
    box-sizing: border-box;
    background-color: black;
}

body>.container, .page-footer {
    background-color: #000000;
    padding: 10px;
    margin-bottom: 10px;
    color: #b9b4b4;
}

.TagCloud li {
    display: inline-block;
    background: #F7F7F7;
    background: rgba(95, 94, 94, 0.51);
}

.nav-pills>li.active>a, .nav-pills>li.active>a:hover, .nav-pills>li.active>a:focus, .FilterMenu .Active>a, .FilterMenu .Active>a:hover, .FilterMenu .Active>a:focus, .breadcrumb, .Breadcrumbs, .VotingSort, .pagination>.active>a, .pagination>.active>span, .pagination>.active>a:hover, .pagination>.active>span:hover, .pagination>.active>a:focus, .pagination>.active>span:focus, .NumberedPager .Highlight, .NumberedPager .Highlight:hover, .NumberedPager .Highlight:focus, .pagination>.disabled>span, .pagination>.disabled>span:hover, .pagination>.disabled>span:focus, .pagination>.disabled>a, .pagination>.disabled>a:hover, .pagination>.disabled>a:focus, .NumberedPager>span, .NumberedPager>span:hover, .NumberedPager>span:focus {
    background-color: #303030;
}

.panel-body, .Empty, .NoResults, .MessageForm .Form-Body, .MessageList .Item-Body, .MessageList.Conversation .Message {
    padding: 15px;
    background-color: black;
}

.Item .Title, .Item .Title a {
    color: #c71414;
}

input.disabled, input:disabled, select.disabled, select:disabled, textarea.disabled, textarea:disabled {
    background: rgba(0, 0, 0, 0.5);
    border-color: rgba(222,222,222,.75);
    -webkit-box-shadow: inset 0 1px 2px rgba(0,0,0,.04);
    box-shadow: inset 0 1px 2px rgba(0,0,0,.04);
    color: rgb(0, 112, 255);
}

#display_name {
    background-color: #252525 !important;
}

li[ID*="Activity_"], li[ID*="ActivityComment_"], article[ID*="post-"] {
    background-color: black !important;
    padding-top: 5px;
    margin-bottom: 10px !important;
}

body {
    background:url(http://i.imgur.com/ypzDMGB.jpg) no-repeat center fixed;
    background-size:cover;-o-background-size:cover;
    -webkit-background-size:cover !important;
}

.stacked-list-item.active .collapsible-body {
    background-color: #151515 !important;
}


}

@-moz-document domain("wuxiaworld.com") {
body::-webkit-scrollbar {
    width: 0.5em;
}
 
body::-webkit-scrollbar-track {
    -webkit-box-shadow: inset 0 0 6px dimgray;
    
}

::-webkit-scrollbar-button {
      background-color: dimgray;
 color: black;   
}

::-moz-selection {
    background-color: #000000 !important;
    color: #c71e1e !important;
}

/* Works in Safari */

::selection {
    background-color: transparent !important;
    color: Goldenrod !important;
}

input::-webkit-input-placeholder,
textarea::-webkit-input-placeholder {
    color: white !important;
    text-shadow: none!important;;
    -webkit-text-fill-color: initial!important;;
}

/*  */.morph:hover {
    border-radius: 50%;
    -webkit-transform: rotate(360deg);
    -moz-transform: rotate(360deg);
    -o-transform: rotate(360deg);
    -ms-transform: rotate(360deg);
    transform: rotate(360deg);
}

::-webkit-scrollbar-track, div.CodeMirror-vscrollbar {
    background-color: black !important;
        border-radius: 5px !important;
}

::-webkit-scrollbar-thumb {
    border-radius: 10px !important;
    background-color: #D62929;
    background-image: -webkit-linear-gradient(90deg, rgba(0, 0, 0, 0.52), rgba(0, 0, 0, 0), rgba(139, 0, 0, 0), rgba(0, 0, 0, 0), rgba(0, 0, 0, 0.52));
}

#primary {
    width: 884px;
    float: left;
    padding: 0!important;
    margin: 0!important;
    clear: right;
}
}
