css:
@-moz-document domain("loots.com") {
body,
html {
    background: #111 !important;
}
.lts-lootility-body {
    background-color: #111 !important;
    color: #fff !important;
}
.lts-box {
    background-color: #1c1c1c !important;
    border: solid 2px #444 !important;
    border-radius: 0px !important;
    padding: 16px !important;
    box-shadow: 0 0 15px #000 !important;
}
.lts-list--logo img {
    border-radius: 0px !important;
    width: 100% !important;
    border: 2px solid #444 !important;
}
.lts-lootility-header {
    background-color: #1c1c1c !important;
    border-bottom: none !important;
    box-shadow: 0 0 10px #000 !important;
}
.lts-nav-main {
    background-color: #1c1c1c !important;
    box-shadow: 0 0 15px #000 !important;
}
.lts-nav-main .is-active a {
    border-bottom: solid 1px #444 !important;
    opacity: 0.5 !important;
    text-decoration: none;
}
.lts-lootility-nav-sub>li {
    border-radius: 0px !important;
    border: solid 2px #444 !important;
}
.lts-lootility-nav-sub>li.is-active>span a,
.lts-lootility-nav-sub>li>span a:hover:not(.is-disabled),
.lts-lootility-nav-sub>li>span.is-active a {
    background-color: #2a2a2a !important;
    color: #fff !important;
}
.lts-lootility-nav-sub--secondary ul {
    background-color: #1c1c1c !important;
}
.lts-lootility-nav-sub>li>span a {
    background-color: #1c1c1c !important;
}
.font-sansN2-base {
    color: #fff !important;
}
.lts-lootility-nav-sub ul {
    background-color: #1c1c1c !important;
}
.selectize-dropdown,
.selectize-dropdown.form-control {
    background: #1c1c1c !important;
}
.selectize-dropdown .active {
    background-color: #2a2a2a !important;
    color: #fff !important;
}
.lts-languages--item {
    background-color: #2a2a2a !important;
}
.lts-lootility-header svg {
    fill: #2a2a2a !important;
}
.lts-pager ul li a:hover,
.lts-pager ul li.is-active a {
    background-color: #2a2a2a !important;
}
.lts-pager ul li a {
    background-color: #222 !important;
    border: solid 2px #444 !important;
    border-radius: 0px !important;
}
.lts-table td:first-child,
.lts-table th:first-child {
    border-radius: 0px !important;
}
.lts-table td:last-child,
.lts-table th:last-child {
    border-radius: 0px !important;
}
.lts-nav-sidebar {
    background-color: #1c1c1c !important;
    border-radius: 0px !important;
    border: solid 2px #444 !important;
    box-shadow: 0 0 15px #000 !important;
}
.lts-lootility-account form label {
    color: #fff!important;
}
.lts-broadcaster--software-label {
    color: #ccc !important;
}
.lts-list--transactions>li {
    border: solid 2px #444 !important;
    border-radius: 0px !important;
    box-shadow: 0 0 5px #000 !important;
}
}
