@-moz-document url-prefix("http://www.webhostingtalk.ir/"), url-prefix("http://webhostingtalk.ir/") {
    
@font-face {
  font-family: Vazir;
  src: url('https://cdn.rawgit.com/rastikerdar/vazir-font/v13.0.1/dist/Vazir.eot');
  src: url('https://cdn.rawgit.com/rastikerdar/vazir-font/v13.0.1/dist/Vazir.eot?#iefix') format('embedded-opentype'),
       url('https://cdn.rawgit.com/rastikerdar/vazir-font/v13.0.1/dist/Vazir.woff2') format('woff2'),
       url('https://cdn.rawgit.com/rastikerdar/vazir-font/v13.0.1/dist/Vazir.woff') format('woff'),
       url('https://cdn.rawgit.com/rastikerdar/vazir-font/v13.0.1/dist/Vazir.ttf') format('truetype');
  font-weight: normal;
}

@font-face {
  font-family: Vazir;
  src: url('https://cdn.rawgit.com/rastikerdar/vazir-font/v13.0.1/dist/Vazir-Bold.eot');
  src: url('https://cdn.rawgit.com/rastikerdar/vazir-font/v13.0.1/dist/Vazir-Bold.eot?#iefix') format('embedded-opentype'),
       url('https://cdn.rawgit.com/rastikerdar/vazir-font/v13.0.1/dist/Vazir-Bold.woff2') format('woff2'),
       url('https://cdn.rawgit.com/rastikerdar/vazir-font/v13.0.1/dist/Vazir-Bold.woff') format('woff'),
       url('https://cdn.rawgit.com/rastikerdar/vazir-font/v13.0.1/dist/Vazir-Bold.ttf') format('truetype');
  font-weight: bold;
}

@font-face {
  font-family: Vazir;
  src: url('https://cdn.rawgit.com/rastikerdar/vazir-font/v13.0.1/dist/Vazir-Light.eot');
  src: url('https://cdn.rawgit.com/rastikerdar/vazir-font/v13.0.1/dist/Vazir-Light.eot?#iefix') format('embedded-opentype'),
       url('https://cdn.rawgit.com/rastikerdar/vazir-font/v13.0.1/dist/Vazir-Light.woff2') format('woff2'),
       url('https://cdn.rawgit.com/rastikerdar/vazir-font/v13.0.1/dist/Vazir-Light.woff') format('woff'),
       url('https://cdn.rawgit.com/rastikerdar/vazir-font/v13.0.1/dist/Vazir-Light.ttf') format('truetype');
  font-weight: 300;
}

@font-face {
  font-family: Vazir;
  src: url('https://cdn.rawgit.com/rastikerdar/vazir-font/v13.0.1/dist/Vazir-Medium.eot');
  src: url('https://cdn.rawgit.com/rastikerdar/vazir-font/v13.0.1/dist/Vazir-Medium.eot?#iefix') format('embedded-opentype'),
       url('https://cdn.rawgit.com/rastikerdar/vazir-font/v13.0.1/dist/Vazir-Medium.woff2') format('woff2'),
       url('https://cdn.rawgit.com/rastikerdar/vazir-font/v13.0.1/dist/Vazir-Medium.woff') format('woff'),
       url('https://cdn.rawgit.com/rastikerdar/vazir-font/v13.0.1/dist/Vazir-Medium.ttf') format('truetype');
  font-weight: 500;
}

@font-face {
  font-family: Vazir;
  src: url('https://cdn.rawgit.com/rastikerdar/vazir-font/v13.0.1/dist/Vazir-Thin.eot');
  src: url('https://cdn.rawgit.com/rastikerdar/vazir-font/v13.0.1/dist/Vazir-Thin.eot?#iefix') format('embedded-opentype'),
       url('https://cdn.rawgit.com/rastikerdar/vazir-font/v13.0.1/dist/Vazir-Thin.woff2') format('woff2'),
       url('https://cdn.rawgit.com/rastikerdar/vazir-font/v13.0.1/dist/Vazir-Thin.woff') format('woff'),
       url('https://cdn.rawgit.com/rastikerdar/vazir-font/v13.0.1/dist/Vazir-Thin.ttf') format('truetype');
  font-weight: 100;
}

  body {
      font-family: 'Vazir';
  }
  
  .forumhead+.childforum .L2:first-child .forumrow, .forumhead+.L2 .forumrow {
      background: rgb(255,255,255);
      border-bottom: 0px;
      border-right: 0px;
      border-left: 0px;
      border-top: 0px;
      box-shadow: 0px 0px 0px;
  }
  .forumbit_nopost .forumbit_nopost .forumrow, .forumbit_post .forumrow {
      background: rgb(255,255,255);
      border-bottom: 0px;
      border-right: 0px;
      border-left: 0px;
      border-top: 0px;
      box-shadow: 0px 0px 0px;
  }
  body a {
      color: #417394;
      text-decoration: none;
  }
  body a:hover {
      color: #59abe3;
      text-decoration: none;
  }
  .above_body {
      background: #59abe3;
  }
  .forumhead a:hover {
      text-decoration: none;
  }
  .forumtitle a {
      color: #404040;
      font-weight: normal;
  }
  .forumtitle a:hover {
      color: #59abe3;
      font-weight: normal;
      text-decoration: none;
  }
  .forumbit_nopost .forumhead a.collapse img {
      position: absolute;
      top: -8px;
      left: 3px;
      display: block;
      width: 28px;
      height: 28px;
      overflow: hidden;
      float: left;
      clear: right;
  }
  #forums .L1 .forumhead a {
      font: normal 17px 'Vazir', Tahoma, Calibri, Verdana, Geneva, sans-serif;
  }
  .forumhead {
      background: #59abe3;
      height: 50px;
      border-radius: 4px;
      margin: 0 0;
      color: #FFF;
      font: 16px 'Vazir';
      box-sizing: border-box;
      -moz-box-sizing: border-box;
      -o-box-sizing: border-box;
      -webkit-box-sizing: border-box;
      text-shadow: 1px 1px #59abe3;
      padding-top: 9px;
      padding-right: 30px;
      position: relative;
      line-height: 1;
      margin-bottom: 15px;
      z-index: 9999;
      margin-top: 15px;
      display: inline-block;
      width: 100%;
  }
  .navbar {
      background: #404040;
  }
  .navtabs {
      background: #404040;
      width: 100%;
      height: 50px;
      font: 15px 'Vazir';
      color: white;
      padding: 16px 20px;
      box-sizing: border-box;
      -moz-box-sizing: border-box;
      -o-box-sizing: border-box;
      -webkit-box-sizing: border-box;
      line-height: 1;
      font-family: 'Vazir';
  }
  .navtabs li a.navtab:hover {
      background: #404040;
      color: white;
  }
  .navtabs li {
      border: 0px;
  }
  .navtabs li.selected {
      border: 0px;
  }
  .navtabs li.selected a.navtab {
      background: #404040;
      border: 0px;
      color: white;
  }
  .navtabs li a.navtab {
      background: #404040;
      border: 0px;
  }
  .globalsearch {
      line-height: 26px;
      padding: 16px 20px;
  }
  .globalsearch .textboxcontainer {
      border: 0px;
  }
  .globalsearch .buttoncontainer {
      border: 0px;
  }
  .globalsearch .textboxcontainer span {
      border: 0px;
  }
  .globalsearch .buttoncontainer span {
      border: 0px;
  }
  .navbar_advanced_search {
      display: none;
  }
  .toplinks ul.isuser li a:hover {
      padding-top: 3px;
      padding-bottom: 3px;
      padding-right: 4px;
      padding-left: 4px;
      background: none;
      color: rgb(255, 255, 255);
  }
  .toplinks .notifications a.popupctrl:hover,
  .toplinks .nonotifications a.popupctrl:hover,
  .toplinks .nonotifications a.popupctrl.active,
  .toplinks .notifications a.popupctrl.active {
      padding-top: 3px;
      padding-bottom: 3px;
      padding-right: 4px;
      padding-left: 15px;
      background: url(http://www.webhostingtalk.ir/images/misc/arrow.png) no-repeat left center
  }
  .forumthreadpost {
      font-size: 0px;
  }
  .forumlastpost {
      font-size: 0px;
  }

  .forumbit_nopost .forumhead .forumtitle, .forumbit_nopost .forumhead span, .forumbit_nopost .forumhead .collapse, .forumbit_post .forumhead h2 span {
      font-size: 0px;
  }

  .subforumdescription {
      display: none;
  }
  .forumbit_nopost .forumhead .forumtitle a {
      font: normal 17px 'Vazir', Tahoma, Calibri, Verdana, Geneva, sans-serif;
  }

  .mainbox {
      background: white;
      border: 0px;
      padding: 1px;
      margin: 1px;
  }

  .tabs li {
      list-style: none;
      float: right;
      line-height: 27px;
      border-left: 0px;
      height: 27px;
      font-size: 11px;
      cursor: pointer;
  }

  .tabs {
      padding-bottom: 26px;
      margin-bottom: 15px;
      background: #EFEFEF;
      border-top: 0px;
      border-bottom: 0px;
  }

  .wgo_block .blockhead {
      height: auto;
      font: normal 17px 'Vazir', Tahoma, Calibri, Verdana, Geneva, sans-serif;
      background: #707070;
      border: 1px solid #606060;
      padding-top: 7px;
      padding-bottom: 8px;
  }

  .forum_info .blockhead {
      height: auto;
      font: normal 17px 'Vazir', Tahoma, Calibri, Verdana, Geneva, sans-serif;
      background: #707070;
      border: 1px solid #606060;
      padding-top: 7px;
      padding-bottom: 8px;
  }

  .threadlisthead {
      background: #616161;
      height: 50px;
      border-radius: 4px;
      margin: 0 0;
      color: #FFF;
      font: 16px 'Vazir';
      box-sizing: border-box;
      -moz-box-sizing: border-box;
      -o-box-sizing: border-box;
      -webkit-box-sizing: border-box;
      text-shadow: 1px 1px #3F3F3F;
      padding-top: 12px;
      padding-right: 30px;
      position: relative;
      line-height: 1;
      margin-bottom: 15px;
      z-index: 9999;
      margin-top: 15px;
      display: inline-block;
      width: 100%;
  }

  .forum_info a.collapse { display: none; }

  .postbit .posthead, .postbitlegacy .posthead, .eventbit .eventhead {
      clear: both;
      display: block;
      float: right;
      width: 100%;
      color: white;
      margin: 0px;
      font: normal 12px Tahoma;
      padding: 13px 0px;
      border-radius: 5px 5px 0px 0px;
      margin-bottom: -4px;
      z-index: 1;
      position: relative;
      box-shadow: 0px 0px #7C7C7D,0px 5px #0077aa !important;
      border-bottom: 0 solid #383A38 !important;
      background: #3498db;
      text-shadow: 1px 1px #0077aa;
      border: none;
      padding-left: 1px;
  }

  .postbit .posthead .nodecontrols, .postbitlegacy .posthead .nodecontrols, .eventbit .eventhead .nodecontrols {
      position: absolute;
      color: #FFFFFF;
      left: 0;
      top: 9px;
      width: 50%;
      padding: 4px 10px 0;
      text-align: left;
      font: normal 12px Tahoma;
      vertical-align: middle;
  }

  .postbitlegacy .postdetails, .eventbit .eventdetails {
      float: right;
      width: 100%;
      background: #FAFAFA none;
      border-radius: 5px;
      border: 1px solid #DADADA;
      padding-bottom: 34px;
  }

  .postbitlegacy .userinfo {
      float: right;
      position: relative;
      width: 180px;
      padding: 22px 10px 5px;
      font-size: 11px;
      display: block;
      clear: both;
      background: white;
  }

  .username_container {
      padding: 2px 0;
      text-align: center;
  }

  .usertitle {
      padding: 2px 0;
      text-align: center;
  }

  .postuseravatar {
      text-align: center;
      border-radius: 10px;
      background: whiteSmoke;
      padding: 5px;
      border: 1px solid #E9E9E9;
      margin-bottom: 10px;
      max-width: 150px;
  }

  .postuseravatar img {
      border-radius: 10px;
  }

  .userinfo_extra {
      border: 1px solid #ECECEC;
      border-radius: 5px;
      padding: 5px 4px;
      background: rgb(248,248,248);
      box-shadow: 0px 1px rgb(252,252,252) inset;
      margin-bottom: 5px;
  }

  .postbitlegacy .userinfo .userinfo_extra .legacytext {
      display: none;
  }

  .postbitlegacy .userinfo .userinfo_extra {
      text-align: center;
      width: auto;
      float: none;
      clear: both;
  }

  .postbitlegacy .userinfo .postuseravatar, .eventbit .userinfo .eventuseravatar {
      display: block;
      margin: 3.33333333333px auto 0;
      text-align: center;
      width: auto;
      float: none;
      clear: both;
  }

  .postbitlegacy .userinfo .username_container {
      margin-bottom: 8px;
  }

  .threadbit .nonsticky, .threadbit .discussionrow {
      background: #ffffff;
      border: 0px;
      border-top: 0;
  }

  .threadbit .sticky {
      background: #F2F6F8;
      border: 0px;
      border-top: 0;
  }

  .threadbit, .threadlisthead {
      box-shadow: 0px 0px 0px; 
  }

  .threadbit .alt {
      border-radius: 5px;
      background: #f2f6f8;
  }
  #WzTtDiV { display: none; }
  .newcontent_textcontrol {
      background: #0077aa;
      -moz-border-radius: 3px;
      -webkit-border-radius: 3px;
      border-radius: 3px;
      -moz-box-shadow: 0 3px 8px #dddddd;
      -webkit-box-shadow: 0 3px 8px #dddddd;
      box-shadow: 0 3px 8px #dddddd;
      font: normal 14px;
      color: rgb(255,255,255);
      padding: 4px 15px 4px 15px;
      border: 0;
      display: block;
      float: right;
      clear: left;
  }

  .newcontent_textcontrol:hover {
      background: #3498db;
  }

  .footer {
      background: #404040;
      width: 100%;
      height: 50px;
      font: 15px 'Vazir';
      color: white;
      padding: 16px 20px;
      box-sizing: border-box;
      -moz-box-sizing: border-box;
      -o-box-sizing: border-box;
      -webkit-box-sizing: border-box;
      line-height: 1;
      font-family: 'Vazir';
  }

  .footer_select { display: none; }

  .footer_copyright {
      direction: ltr;
  }
  .footer_copyright:before {
      display: block;
      content: 'WebHostingTalk.ir Take Into Flat! (TIF) v1.3 - Designed by TATSERVER.NET';
  }
}
