css:
@-moz-document domain('baidu.com'){
#content_left > div.result,#content_left > div.result-op{
  	border: 1px solid rgba(0,0,0,.2)!important;
  	border-radius:4px!important;
  	padding:10px!important;
	margin-bottom:10px!important;
}
#content_right #con-ar div.opr-recommends-merge-panel
,#content_right #con-ar div.opr-recommends-panel
,#content_right #con-ar div.op-wenda-dalists
{
	border-left: 1px solid rgba(0,0,0,.2)!important;
	border-right: 1px solid rgba(0,0,0,.2)!important;
	border-bottom: 1px solid rgba(0,0,0,.2)!important;
	border-bottom-left-radius:4px!important;
	border-bottom-right-radius:4px!important;
  	padding:1px 10px 10px 10px !important;
	margin-bottom:20px!important;
  	width:calc(100%);
}
#content_right #con-ar div.cr-title{
	border-left: 1px solid rgba(0,0,0,.2)!important;
	border-right: 1px solid rgba(0,0,0,.2)!important;
	border-top: 1px solid rgba(0,0,0,.2)!important;
	border-top-left-radius:4px!important;
	border-top-right-radius:4px!important;
  	padding:8px 8px 0px 8px !important;
	width:calc(100% + 4px);
}

#content_right div.result-op{
	margin-bottom:20px !important;
}
/*补充对 右侧上下间距不一致问题1*/
#content_right div.result_hidden
,#content_right  div.cr-content>style + div
,#content_right div.result-op[tpl='right_personinfo']{
	margin-top:10px !important;
}
/*补充对 右侧上下间距不一致问题2*/
#content_right div.result-op[tpl='right_toplist']
,#content_right div.opr-personinfo-info
,#content_right #con-ar div.result-op:not(:first-child)
{
margin-top:-10px !important;
}
/*右侧table*/
#content_right div.cr-content table
{
	border-left: 1px solid rgba(0,0,0,.2)!important;
    border-right: 1px solid rgba(0,0,0,.2)!important;
    border-bottom: 1px solid rgba(0,0,0,.2)!important;
    padding-top: 5px!important;
    margin-top: -10px!important;
    border-collapse: separate!important;
    width: calc(100% + 22px);
    border-bottom-left-radius: 4px;
    border-bottom-right-radius: 4px;
}

#content_right div.c-clearfix{
	border-left: 1px solid rgba(0,0,0,.21)!important;
	border-right: 1px solid rgba(0,0,0,.2)!important;
	padding-top:5px!important;
	margin-top:-10px!important;
	padding-left:10px!important;
  	width:calc(100% + 10px);
}

#content_right div.cr-content table thead tr th
,#content_right div.cr-content table tbody tr td{
	padding:5px 8px 5px 8px !important;
}
/*右侧table最后一个tr去除boder*/
#content_right div.cr-content table tbody tr:last-child>td:nth-child(1),#content_right div.cr-content table tbody tr:last-child>td:nth-child(2){
	border: 0;
}
#content_right div.cr-content div.cr-title div.opr-toplist-more{
    right: -5px !important;
    top: 5px !important;
}

#content_right div.cr-content table+div
,#content_right div.c-gap-top-small.opr-toplist-info
,#content_right tr>td>table
{
	display:none!important;
}
#con-ar a.c-feedback{
	display:none!important;
}
}
