css:
@-moz-document url-prefix("https://www.baidu.com/s?") {

    /*
     * ver:1.0.3 
     * 兼容资讯类搜索
     */
    
    
    /** ------------------左侧-------------- **/
    
    
    /* 去除原边框样式 */
    .c-container .c-border
    ,.c-group-wrapper
    ,div[class^="single-card-wrapper_"]
    ,div[class^="re-box"]
    ,#op_wiseapp
    {
        border:none !important;
        padding-top:0px !important;
        padding-bottom:0px !important;
        box-shadow:none !important;
    }
    
    /* 所有内容左对齐 */
    .c-group-wrapper{
        margin-left: 0px !important;
        margin-right: 0px !important;
    }
    
    /* 统一左侧宽度 */
    #container.sam_newgrid #content_left
    ,.new-pmd.c-container
    ,#content_left > div.result
    ,#content_left > div.old-pmd
    ,#content_left > div.result-op
    ,#content_left > .c-group-wrapper
    ,#content_left > div:not([class])
    {
        width:576px !important;
    }
    
    /* 组-头宽度调整，阻止超出宽度 */
    .c-group-wrapper .c-container > [class^="header-right_"]{
        right:16px !important;
    }
    
    /** 添加边框 **/
    #content_left > div.c-container
    ,#content_left > .result-op
    ,#content_left > .old-pmd
    ,#content_left > div.c-group-wrapper
    ,#content_left > div:not([class]) div.c-container
    {
        border:1px solid #dddddddd !important;
        border-radius:6px !important;
        padding:10px;
        display:block;
    }
    
    /** 对table类型，重新设置宽度 */
    #content_left > table.result-op{
        width:calc(576px + 20px + 2px) !important;/* width + padding + border */
    }
    
    /* 统一下边距 */
    #content_left > .result-op
    ,#content_left > .old-pmd
    ,#container.sam_newgrid #content_left .result
    ,#content_left > div:not([class]) div.c-container
    ,#content_left > div:not([class]) > div
    ,#content_left > div.c-group-wrapper
    {
        margin-bottom:17px !important;
    }
    
    
    /** ------------------右侧-------------- **/
    
    #content_right{
      min-width:370px !important;
    }
    
    /* 隐藏 - 非内容区域信息 */
    #content_right tbody > tr > td > div:not(#con-ar)
    {
        display:none !important;
    }
    
    /** 移除原样式 */
    .opr-recommends-merge-margin-up-small{
        margin-top:-13px !important;
    }
    
    /* 调整原宽度 */
    #content_right .cr-content{
        min-width:370px !important;
      width: inherit;
    }
    
    /* 直属第一层DIV */
    #content_right > div[class]:not([id='s_side_wrapper']):not(#con-ar){
        border: 1px solid #dddddddd !important;
        border-radius: 6px !important;
        padding: 10px 10px 0px;
        width: 366px;
        display: block;
        margin-bottom: 20px;
    }
    
    /* 资讯类搜索结果 - 右侧空内容 */
    #content_right > div[id^='ecomAdDiv_']
    {
        display:none !important;
    }
    
    /* Table - 头 */
    #content_right #con-ar .cr-content > div > div.cr-title{
        margin-bottom:0px !important;
        padding:6px 10px 8px;
        min-width: 370px;
    
        border-top:1px solid #dddddddd;
        border-left:1px solid #dddddddd;
        border-right:1px solid #dddddddd;
        border-top-left-radius:6px;
        border-top-right-radius:6px;
    }
    
    /* Table - 体 */
    #content_right #con-ar .cr-content > div > div.cr-title + div[class^="opr-"]{
        margin-bottom:15px !important;
        padding:0px 10px 8px;
        min-width: 370px;
    
        border-bottom:1px solid #dddddddd;
        border-left:1px solid #dddddddd;
        border-right:1px solid #dddddddd;
        border-bottom-left-radius:6px;
        border-bottom-right-radius:6px;
    }
    
    /* Table - 中特殊的div */
    #content_right #con-ar .cr-content:not(.new-pmd){
        border: 1px solid #dddddddd !important;
        border-radius: 6px !important;
        padding: 10px 10px 10px;
        display: block;
        margin-bottom: 20px;
    }
    
    
    /** ------------------去广告-------------- **/
    
    /* 左侧推广 - 暂不确定范围 */
    #content_left > div:not([class]) > div._2z1q32z {
        content-visibility: hidden;
        margin-bottom: 0px !important;
    }
    
    /* 结果中穿插的 - 其他人还在搜 */
    #content_left > div[tpl='recommend_list'] {
        display:none;
    }
    
    
    /** ---------------------------已知bugs-------------------------- */
    
    /* 移除空内容 - 目前不生效，因为内容不为empty */
    /* #content_left > .old-pmd:empty {
        display:none !important;
    } */
    
    }
