css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("horriblesubs.info") {
  
  /*
  Alternative banners that I could use:
  http://imgur.com/a/6YyJA
  http://imgur.com/a/ga1nG
  http://horriblesubs.info/images/b/gundam_banner2.jpg
  */

  body
  {
    background-color: /*[[background]]*/ !important;
  }
  div,
  #wrapper
  {
    background-color: #222 !important;
  }
  body *
  {
    color: #bbb !important;
  }
  
  a
  {
    background-color: transparent !important;
    -webkit-transition: none !important;
    -moz-transition: none !important;
    -o-transition: none !important;
    transition: none !important;
    border-bottom: none !important;
  }
  /*a:visited
  {
    color: #ff8d1e !important;
  }*/
  a:hover
  {
    color: #1e90ff !important;
    text-decoration: none !important;
  }
  
  /*.linkful a:visited,
  .rls-label a:visited,*/
  #main a:visited,
  #sidebar a:visited
  {
    color: #ff8d1e !important;
  }
  #main a:hover,
  #sidebar a:hover
  {
    color: #1e90ff !important;
  }
  
  input
  {
    background-color: #333 !important;
    color: #fff !important;
  }
  
  /* Banner images */
  #headerimg,
  #mban img,
  #sqban img
  {
    filter: brightness(80%);
  }
  
  .announcement,
  .schedule-today
  {
    background-color: #333 !important;
    color: #bbb !important;
  }
  
  .release-info:hover,
  .release-links:hover
  {
    background-color: #333 !important;
  }
  
  .linkless
  {
    color: #111 !important;
  }
  .linkless:hover
  {
    background-color: transparent !important;
  }
  
  .shows-morebox:hover,
  .morebox:hover,
  .morebox-batch:hover,
  .refreshlink:hover,
  .shows-clearlink:hover
  {
    background-color: #333 !important;
  }
  
  /* List of shows */
  .alpha-nav:hover,
  .linkful:hover
  {
    background-color: #333 !important;
  }
  
  /* Twitter link */
  .menu-item-130418
  {
    background-color: #333 !important;
  }
}

@-moz-document url("http://free.timeanddate.com/clock/i4vybhfo/n137/fn17/th1/ta1")
{
  * {
	   background: none !important;
    color: #bbb !important;
  }
}

@-moz-document regexp("(https|http)://.*disqus.com/embed/comments/.*(horriblesubs.info).*")
{
  .highlighted {
    background: none !important;
  }
  
  /* Text */
  div.post-message p {
    color: #eee !important;
  }
  
  /* Load new comments buttons */
  .alert, .btn {
    background-color: #111 !important;
    color: #bbb !important;
  }
  .alert:hover, .btn:hover {
    background-color: #090909 !important;
  }
  
  span.badge.moderator
  {
    background-color: #bbb !important;
    filter: brightness(70%);
  }
  
  /* Links */
  a:link,
  .nav .dropdown-toggle,
  .nav > ul > li > a,
  .nav > ul > li > div a,
  .post-content footer .voting .vote-down,
  .post-content footer a,
  h2,
  /* Comment count */
  .comment-count
  {
    color: #7E97AC !important;
  }
  
  /* Number on the right side of "Recommend" */
  .nav-secondary > ul > li.recommend .thread-likes a .label-count
  {
    background: #111 !important;
    color: #aaa !important;
  }
  
  .textarea-wrapper,
  .post-actions
  {
    background: #333 !important;
    border-color: #444 !important;
  }
  .textarea
  {
    color: #bbb !important;
  }
  
  .post-content .post-menu .dropdown-toggle
  {
    border-color: #111 !important;
  }
}
